SET client_encoding TO 'UTF8';





CREATE OR REPLACE PROCEDURE inserir_analista_exec (nr_seq_ordem_p bigint, nm_usuario_p text) AS $body$
DECLARE



cd_funcao_w		bigint;
nr_seq_grupo_des_w	bigint;
nm_usuario_grupo_w	varchar(255);
nm_usuario_exec_w	varchar(255);
dt_fim_execucao_w		timestamp;
nr_seq_exec_w		bigint;
ie_usuario_exec_w		varchar(1);


BEGIN

select	max(cd_funcao),
	max(nr_seq_grupo_des)
into STRICT	cd_funcao_w,
	nr_seq_grupo_des_w
from	man_ordem_servico
where	nr_sequencia = nr_seq_ordem_p;

select 	max(nm_usuario_grupo)
into STRICT	nm_usuario_grupo_w
from	usuario_grupo_des
where	nr_seq_grupo = nr_seq_grupo_des_w
and	ie_funcao_usuario = 'M';

select 	CASE WHEN count(*)=0 THEN 'N'  ELSE 'S' END ,
	max(dt_fim_execucao),
	max(nr_sequencia)
into STRICT	ie_usuario_exec_w,
	dt_fim_execucao_w,
	nr_seq_exec_w
from	man_ordem_servico_exec
where	nr_seq_ordem = nr_seq_ordem_p
and	nm_usuario_exec = nm_usuario_grupo_w;

	if (ie_usuario_exec_w = 'S') then
	begin
		if (dt_fim_execucao_w IS NOT NULL AND dt_fim_execucao_w::text <> '') then
			update 	man_ordem_servico_exec
			set	dt_fim_execucao  = NULL
			where	nr_sequencia = nr_seq_exec_w;
		end if;
	end;
	end if;

	if (nm_usuario_grupo_w IS NOT NULL AND nm_usuario_grupo_w::text <> '')
	and (ie_usuario_exec_w = 'N') then
		insert into man_ordem_servico_exec(	nr_sequencia,
							nr_seq_ordem,
							dt_atualizacao,
							nm_usuario,
							nm_usuario_exec,
							qt_min_prev,
							nr_seq_funcao,
							nr_seq_tipo_exec)
					values (		nextval('man_ordem_servico_exec_seq'),
							nr_seq_ordem_p,
							clock_timestamp(),
							nm_usuario_p,
							nm_usuario_grupo_w,
							10,
							31,
							1);
	end if;

	if (coalesce(nm_usuario_grupo_w::text, '') = '') then
		CALL Wheb_mensagem_pck.exibir_mensagem_abort(286261);
	end if;

commit;
end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
