CREATE OR REPLACE PROCEDURE inserir_anexo_mat_cotacao ( nr_cot_compra_p bigint, nm_usuario_p text) AS $body$
DECLARE


cd_material_w		integer;
ds_arquivo_w		varchar(255);
qt_existe_w		bigint;

c01 CURSOR FOR
SELECT	cd_material
from	cot_compra_item
where	nr_cot_compra = nr_cot_compra_p;

c02 CURSOR FOR
SELECT	a.ds_arquivo
from	material_anexo a
where	a.cd_material = cd_material_w;


BEGIN

open C01;
loop
fetch C01 into
	cd_material_w;
EXIT WHEN NOT FOUND; /* apply on C01 */
	begin
	open C02;
	loop
	fetch C02 into
		ds_arquivo_w;
	EXIT WHEN NOT FOUND; /* apply on C02 */
		begin

		select	count(*)
		into STRICT	qt_existe_w
		from	cot_compra_anexo
		where	nr_cot_compra = nr_cot_compra_p
		and	ds_arquivo = ds_arquivo_w;

		if (qt_existe_w = 0) then

			insert into cot_compra_anexo(
				nr_sequencia,
				dt_atualizacao,
				nm_usuario,
				dt_atualizacao_nrec,
				nm_usuario_nrec,
				nr_cot_compra,
				ds_arquivo,
				ie_enviar,
				ie_origem)
			values (nextval('cot_compra_anexo_seq'),
				clock_timestamp(),
				nm_usuario_p,
				clock_timestamp(),
				nm_usuario_p,
				nr_cot_compra_p,
				ds_arquivo_w,
				'S', 'U');
		end if;
		end;
	end loop;
	close C02;
	end;
end loop;
close C01;

commit;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
