CREATE OR REPLACE PROCEDURE inserir_anexo_rxt_integracao ( cd_agendamento_externo_p text, nm_usuario_p text, ds_anexo_p text ) AS $body$
DECLARE


cd_pessoa_fisica_w	varchar(15);


BEGIN

	select max(cd_pessoa_fisica)
	into STRICT cd_pessoa_fisica_w
	from rxt_agenda
	where cd_agendamento_externo = cd_agendamento_externo_p;
	
	if (cd_pessoa_fisica_w IS NOT NULL AND cd_pessoa_fisica_w::text <> '') then

		insert into atendimento_paciente_anexo( 
			nr_sequencia,
			nr_atendimento,
			dt_atualizacao,
			nm_usuario,
			dt_atualizacao_nrec,
			nm_usuario_nrec,
			ds_arquivo,
			cd_pessoa_fisica,
			dt_validade,
			nr_seq_documento)
		values (nextval('atendimento_paciente_anexo_seq'),
			null ,
			clock_timestamp(),
			nm_usuario_p,
			clock_timestamp(),
			nm_usuario_p,
			ds_anexo_p,
			cd_pessoa_fisica_w,
			null,
			null);
		
	end if;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
