CREATE OR REPLACE PROCEDURE inserir_atendimento_alertas ( nr_atendimento_p bigint, ds_alerta_p text, nr_seq_tipo_alerta_p bigint, nm_usuario_p text, nr_seq_alerta_p bigint, nr_sequencia_p INOUT bigint) AS $body$
DECLARE


nr_sequencia_w	bigint;


BEGIN
if (nr_atendimento_p IS NOT NULL AND nr_atendimento_p::text <> '') and (nr_atendimento_p > 0) then
	begin
	select	nextval('atendimento_alerta_seq')
	into STRICT	nr_sequencia_w
	;
	
	insert into atendimento_alerta(
		nr_sequencia,
		nr_atendimento,
		dt_alerta,
		dt_atualizacao,
		nm_usuario,
		ds_alerta,
		ie_situacao,
		dt_fim_alerta,
		dt_liberacao,
		dt_inativacao,
		nm_usuario_inativacao,
		ds_justificativa,
		nr_seq_tipo_alerta,
		nr_seq_alerta,
		cd_perfil_ativo)
	values (	
		nr_sequencia_w,
		nr_atendimento_p,
		clock_timestamp(),
		clock_timestamp(),
		nm_usuario_p,
		ds_alerta_p,
		'A',
		null,
		null,
		null,
		null,
		null,
		nr_seq_tipo_alerta_p,
		nr_seq_alerta_p,
		obter_perfil_ativo);
		
	commit;
	
	nr_sequencia_p  := nr_sequencia_w;
	end;
end if;
end;
$body$
