SET client_encoding TO 'UTF8';





CREATE OR REPLACE PROCEDURE inserir_atend_alertas_cih ( nr_atendimento_p bigint, ds_alerta_p text, nm_usuario_p text, nr_prescricao_p bigint default null) AS $body$
DECLARE


nr_sequencia_w		bigint;
cd_prescritor_w		varchar(10);
nr_seq_tipo_alerta_w	bigint;


BEGIN

nr_seq_tipo_alerta_w := Obter_Param_Usuario(896, 22, obter_perfil_ativo, nm_usuario_p, wheb_usuario_pck.get_cd_estabelecimento, nr_seq_tipo_alerta_w);

if (nr_atendimento_p IS NOT NULL AND nr_atendimento_p::text <> '') and (nr_atendimento_p > 0) then

	if (nr_prescricao_p IS NOT NULL AND nr_prescricao_p::text <> '') then
		select 	max(cd_prescritor)
		into STRICT 	cd_prescritor_w
		from 	prescr_medica
		where 	nr_prescricao = nr_prescricao_p;
	end if;

	select	nextval('atendimento_alerta_seq')
	into STRICT	nr_sequencia_w
	;

	insert into atendimento_alerta(
		nr_sequencia,
		nr_atendimento,
		dt_alerta,
		dt_atualizacao,
		nm_usuario,
		ds_alerta,
		ie_situacao,
		dt_fim_alerta,
		dt_liberacao,
		dt_inativacao,
		nm_usuario_inativacao,
		ds_justificativa,
		cd_pessoa_alerta,
		nr_seq_tipo_alerta)
	values (
		nr_sequencia_w,
		nr_atendimento_p,
		clock_timestamp(),
		clock_timestamp(),
		nm_usuario_p,
		substr(ds_alerta_p,1,2000),
		'A',
		null,
		null,
		null,
		null,
		null,
		cd_prescritor_w,
		nr_seq_tipo_alerta_w);

	commit;

end if;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
