CREATE OR REPLACE PROCEDURE inserir_atend_alta_destino ( cd_motivo_alta_p bigint, nr_atendimento_p bigint, cd_destino_alta_p bigint, cd_tipo_alta_home_care_p bigint) AS $body$
DECLARE


cd_evolucao_w  bigint;
ie_gerar_clinical_notes_w varchar(1) := 'N';

BEGIN
ie_gerar_clinical_notes_w := obter_param_usuario(281, 1595, Obter_perfil_ativo, wheb_usuario_pck.get_nm_usuario, coalesce(wheb_usuario_pck.get_cd_estabelecimento, 1), ie_gerar_clinical_notes_w);
if (nr_atendimento_p IS NOT NULL AND nr_atendimento_p::text <> '') then
	begin
	INSERT INTO atend_alta_destino(
        nr_sequencia,
	    nr_atendimento,
	    cd_motivo_alta,
	    cd_destino_alta,
	    cd_tipo_alta_home_care,
        nm_usuario,
        NM_USUARIO_NREC,
        DT_ATUALIZACAO,
        DT_ATUALIZACAO_NREC,
        cd_estabelecimento
	) VALUES (
    nextval('atend_alta_destino_seq'),
	    nr_atendimento_p,
	    cd_motivo_alta_p,
	    cd_destino_alta_p,
	    cd_tipo_alta_home_care_p,
        wheb_usuario_pck.get_nm_usuario,
        wheb_usuario_pck.get_nm_usuario,
        clock_timestamp(),
        clock_timestamp(),
        wheb_usuario_pck.get_cd_estabelecimento
	);
	end;
   select max(cd_evolucao)
   into STRICT cd_evolucao_w
   from atendimento_paciente_inf
   where nr_atendimento = nr_atendimento_p;

if (coalesce(cd_evolucao_w ,0) > 0 and ie_gerar_clinical_notes_w = 'S') then
	CALL clinical_notes_pck.soap_data_after_delete(cd_evolucao_w);
end if;
end if;
end;
$body$
