CREATE OR REPLACE PROCEDURE inserir_atualizar_passaporte ( cd_pessoa_fisica_p pessoa_fisica.cd_pessoa_fisica%type, nm_usuario_p pessoa_fisica.nm_usuario%type, nr_seq_pais_passaporte_p pessoa_fisica_aux.nr_seq_pais_passaporte%type, dt_validade_passaporte_p pessoa_fisica_aux.dt_validade_passaporte%type) AS $body$
DECLARE



nr_sequencia_w		pessoa_fisica_aux.nr_sequencia%type;


BEGIN

select max(nr_sequencia)
into STRICT nr_sequencia_w
from pessoa_fisica_aux
where cd_pessoa_fisica = cd_pessoa_fisica_p;

	begin
	insert into pessoa_fisica_aux(
			nr_sequencia,
			dt_atualizacao,
			nm_usuario,
			dt_atualizacao_nrec,
			nm_usuario_nrec,
			cd_pessoa_fisica,
			nr_seq_pais_passaporte,
			dt_validade_passaporte
		) values (
			coalesce(nr_sequencia_w,nextval('pessoa_fisica_aux_seq')),
			clock_timestamp(),
			nm_usuario_p,
			clock_timestamp(),
			nm_usuario_p,
			cd_pessoa_fisica_p,
			nr_seq_pais_passaporte_p,
			dt_validade_passaporte_p
		);
		exception
		when unique_violation then
		
			update pessoa_fisica_aux 
			set 	nr_seq_pais_passaporte = nr_seq_pais_passaporte_p,
					dt_validade_passaporte = dt_validade_passaporte_p,
					dt_atualizacao_nrec = clock_timestamp(),
					nm_usuario_nrec = nm_usuario_p
			where cd_pessoa_fisica = cd_pessoa_fisica_p;
	end;
  commit;

end;
$body$
