CREATE OR REPLACE PROCEDURE inserir_boletim_informativo ( nr_seq_regra_p bigint, nr_atendimento_p bigint, ie_liberado_p text, cd_perfil_p bigint, nm_usuario_p text) AS $body$
DECLARE


ds_boletim_longo_w	varchar(4000);
nr_seq_tipo_boletim_w	bigint;
nr_sequencia_w		bigint;


BEGIN

select	max(ds_boletim_longo),
	max(nr_seq_tipo_boletim)
into STRICT	ds_boletim_longo_w,
	nr_seq_tipo_boletim_w
from	atend_boletim_padrao
where	nr_sequencia	= nr_seq_regra_p;

select	nextval('atendimento_boletim_seq')
into STRICT	nr_sequencia_w
;

insert into atendimento_boletim(nr_sequencia,
	dt_atualizacao,
	nm_usuario,
	dt_atualizacao_nrec,
	nm_usuario_nrec,
	nr_atendimento,
	dt_boletim,
	dt_liberacao,
	cd_perfil_ativo,
	nr_seq_tipo_boletim,
	ds_boletim,
	NR_SEQ_BOLETIM_PADRAO,
	ie_situacao)
values (nr_sequencia_w,
	clock_timestamp(),
	nm_usuario_p,
	clock_timestamp(),
	nm_usuario_p,
	nr_atendimento_p,
	clock_timestamp(),
	CASE WHEN ie_liberado_p='S' THEN clock_timestamp()  ELSE null END ,
	cd_perfil_p,
	nr_seq_tipo_boletim_w,
	ds_boletim_longo_w,
	nr_seq_regra_p,
	'A');

commit;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
