SET client_encoding TO 'UTF8';





CREATE OR REPLACE PROCEDURE inserir_brasindice_restrito ( cd_laboratorio_p text, cd_medicamento_p text, cd_apresentacao_p text, dt_inicio_vigencia_p timestamp, ie_versao_atual_p text, nm_usuario_p text) AS $body$
DECLARE


nr_sequencia_w		bigint;
cd_material_w		integer;
nr_seq_mat_estrutura_w	bigint;
nr_seq_estrutura_w	bigint;
nr_seq_mat_estr_vig_w	bigint;
dt_final_vigencia_w	timestamp;
ie_atualiza_estrutura_w	varchar(1);
cd_convenio_w		bigint;
dt_inicial_w		timestamp;
dt_final_w		timestamp;

C01 CURSOR FOR
	SELECT	distinct
		cd_material,
		dt_vigencia,
		dt_final_vigencia,
		cd_convenio
	from	material_brasindice
	where	cd_apresentacao	= cd_apresentacao_p
	and	cd_medicamento	= cd_medicamento_p
	and	cd_laboratorio =  cd_laboratorio_p
	order by 1;

c02 CURSOR FOR
	SELECT 	nr_sequencia
	from 	MAT_ESTRUTURA
	where	ie_situacao = 'A'
	and 	coalesce(ie_atualiza_brasindice,'N') = 'S'
	and 	coalesce(ie_atualiza_estrutura_w,'N') = 'S';


BEGIN

--ie_atualiza_estrutura_w:=  nvl(obter_valor_param_usuario(705, 27, obter_perfil_ativo, nm_usuario_p, 0), 'N');
ie_atualiza_estrutura_w:= 'N';

commit;

open C01;
loop
fetch C01 into
	cd_material_w,
	dt_inicial_w,
	dt_final_w,
	cd_convenio_w;
EXIT WHEN NOT FOUND; /* apply on C01 */
	begin

	select	nextval('brasindice_restrito_hosp_seq')
	into STRICT	nr_sequencia_w
	;


	insert into brasindice_restrito_hosp(
		cd_laboratorio,
		cd_medicamento,
		cd_apresentacao,
		dt_inicio_vigencia,
		dt_atualizacao,
		dt_atualizacao_nrec,
		nm_usuario,
		nm_usuario_nrec,
		ie_versao_atual,
		nr_sequencia,
		cd_material,
		cd_convenio,
		dt_vig_relac_ini,
		dt_vig_relac_fim,
		cd_estabelecimento)
	values (
		cd_laboratorio_p,
		cd_medicamento_p,
		cd_apresentacao_p,
		to_date(to_char(dt_inicio_vigencia_p,'dd/mm/yyyy'),'dd/mm/yyyy'),
		clock_timestamp(),
		clock_timestamp(),
		nm_usuario_p,
		nm_usuario_p,
		ie_versao_atual_p,
		nr_sequencia_w,
		cd_material_w,
		cd_convenio_w,
		dt_inicial_w,
		dt_final_w,
		wheb_usuario_pck.get_cd_estabelecimento);

	open C02;
	loop
	fetch C02 into
		nr_seq_estrutura_w;
	EXIT WHEN NOT FOUND; /* apply on C02 */
		begin

		select 	coalesce(max(nr_sequencia),0)
		into STRICT	nr_seq_mat_estrutura_w
		from 	mat_estrutura_cadastro
		where 	nr_seq_estrutura = nr_seq_estrutura_w
		and 	cd_material = cd_material_w;

		if (nr_seq_mat_estrutura_w = 0) then -- Inclui
			select 	nextval('mat_estrutura_cadastro_seq')
			into STRICT	nr_seq_mat_estrutura_w
			;

			insert	into  mat_estrutura_cadastro(NR_SEQUENCIA, NR_SEQ_ESTRUTURA, DT_ATUALIZACAO, NM_USUARIO, DT_ATUALIZACAO_NREC, NM_USUARIO_NREC, CD_MATERIAL)
				values (nr_seq_mat_estrutura_w, nr_seq_estrutura_w, clock_timestamp(), nm_usuario_p, clock_timestamp(), nm_usuario_p, cd_material_w);

			insert into mat_estrutura_cad_vig(
				NR_SEQUENCIA, NR_SEQ_MAT_ESTRUTURA, DT_ATUALIZACAO, NM_USUARIO, DT_ATUALIZACAO_NREC, NM_USUARIO_NREC, DT_INICIO_VIGENCIA, DT_FINAL_VIGENCIA)
				values (nextval('mat_estrutura_cad_vig_seq'), nr_seq_mat_estrutura_w , clock_timestamp(), nm_usuario_p, clock_timestamp(), nm_usuario_p, dt_inicio_vigencia_p, null);

		else

			select 	coalesce(max(nr_sequencia),0)
			into STRICT	nr_seq_mat_estr_vig_w
			from 	mat_estrutura_cad_vig
			where 	nr_seq_mat_estrutura = nr_seq_mat_estrutura_w;

			if (nr_seq_mat_estr_vig_w = 0) then
				insert into mat_estrutura_cad_vig(
					NR_SEQUENCIA, NR_SEQ_MAT_ESTRUTURA, DT_ATUALIZACAO, NM_USUARIO, DT_ATUALIZACAO_NREC, NM_USUARIO_NREC, DT_INICIO_VIGENCIA, DT_FINAL_VIGENCIA)
				values (nextval('mat_estrutura_cad_vig_seq'), nr_seq_mat_estrutura_w , clock_timestamp(), nm_usuario_p, clock_timestamp(), nm_usuario_p, dt_inicio_vigencia_p, null);
			else
				select 	max(dt_final_vigencia)
				into STRICT	dt_final_vigencia_w
				from 	mat_estrutura_cad_vig
				where 	nr_sequencia = nr_seq_mat_estr_vig_w;

				if (dt_final_vigencia_w IS NOT NULL AND dt_final_vigencia_w::text <> '') then
					insert into mat_estrutura_cad_vig(
						NR_SEQUENCIA, NR_SEQ_MAT_ESTRUTURA, DT_ATUALIZACAO, NM_USUARIO, DT_ATUALIZACAO_NREC, NM_USUARIO_NREC, DT_INICIO_VIGENCIA, DT_FINAL_VIGENCIA)
					values (nextval('mat_estrutura_cad_vig_seq'), nr_seq_mat_estrutura_w , clock_timestamp(), nm_usuario_p, clock_timestamp(), nm_usuario_p, dt_inicio_vigencia_p, null);
				end if;

			end if;

		end if;

		insert into W_BRASINDICE_ESTRUT_INT(NR_SEQ_ESTRUTURA, CD_MATERIAL, DT_ATUALIZACAO, NM_USUARIO, DT_VIGENCIA)
			values (nr_seq_mat_estrutura_w, cd_material_w, clock_timestamp(), nm_usuario_p, dt_inicio_vigencia_p);
		commit;

		end;
	end loop;
	close C02;


	end;
end loop;
close C01;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
-- REVOKE ALL ON PROCEDURE inserir_brasindice_restrito ( cd_laboratorio_p text, cd_medicamento_p text, cd_apresentacao_p text, dt_inicio_vigencia_p timestamp, ie_versao_atual_p text, nm_usuario_p text) FROM PUBLIC;

