CREATE OR REPLACE PROCEDURE inserir_brasindice_solucao ( cd_laboratorio_p text, cd_medicamento_p text, cd_apresentacao_p text, dt_inicio_vigencia_p timestamp, ie_versao_atual_p text, nm_usuario_p text) AS $body$
DECLARE


