ie_atualiza_estrutura_w	varchar(1);
cd_convenio_w		bigint;
dt_inicial_w		timestamp;
dt_final_w		timestamp;

C01 CURSOR FOR
	SELECT	distinct
		cd_material,
		dt_vigencia,
		dt_final_vigencia,
		cd_convenio
	from	material_brasindice
	where	cd_apresentacao	= cd_apresentacao_p
	and	cd_medicamento	= cd_medicamento_p
	and	cd_laboratorio =  cd_laboratorio_p
	order by 1;


BEGIN

open C01;
loop
fetch C01 into
	cd_material_w,
	dt_inicial_w,
	dt_final_w,
	cd_convenio_w;
EXIT WHEN NOT FOUND; /* apply on C01 */
	begin

	select	nextval('brasindice_solucao_seq')
	into STRICT	nr_sequencia_w
	;


	insert into brasindice_solucao(
		cd_laboratorio,
		cd_medicamento,
		cd_apresentacao,
		dt_inicio_vigencia,
		dt_atualizacao,
		dt_atualizacao_nrec,
		nm_usuario,
		nm_usuario_nrec,
		ie_versao_atual,
		nr_sequencia,
		cd_material,
		cd_convenio,
		dt_vig_relac_ini,
		dt_vig_relac_fim)
	values (
		cd_laboratorio_p,
		cd_medicamento_p,
		cd_apresentacao_p,
		to_date(to_char(dt_inicio_vigencia_p,'dd/mm/yyyy'),'dd/mm/yyyy'),
		clock_timestamp(),
		clock_timestamp(),
		nm_usuario_p,
		nm_usuario_p,
		ie_versao_atual_p,
		nr_sequencia_w,
		cd_material_w,
		cd_convenio_w,
		dt_inicial_w,
