CREATE OR REPLACE PROCEDURE inserir_cadsus_pend_envio ( cd_pessoa_fisica_p text, nm_usuario_p text ) AS $body$
DECLARE

    nr_seq_interno_w   cadsus_pend_envio.nr_sequencia%TYPE;
	qt_pessoa_pend_w   bigint;

BEGIN

	select count(1)
	into STRICT	qt_pessoa_pend_w
	from	cadsus_pend_envio
	where	cd_pessoa_fisica = cd_pessoa_fisica_p
	and 	ie_pendente = 'S';

	if (qt_pessoa_pend_w = 0) then
		SELECT
			nextval('cadsus_pend_envio_seq')
		INTO STRICT
			nr_seq_interno_w
		;

		INSERT INTO cadsus_pend_envio(
			nr_sequencia,
			dt_atualizacao,
			nm_usuario,
			dt_atualizacao_nrec,
			nm_usuario_nrec,
			cd_pessoa_fisica,
			ie_pendente
		) VALUES (
			nr_seq_interno_w,
			clock_timestamp(),
			nm_usuario_p,
			clock_timestamp(),
			nm_usuario_p,
			cd_pessoa_fisica_p,
			'S'
		);

		COMMIT;
	end if;

END;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
