CREATE OR REPLACE PROCEDURE inserir_cardapio_ordem_prod ( nr_seq_odem_p bigint, nr_seq_cardapio_p bigint, nm_usuario_p text, cd_material_p bigint default null, dt_inicio_p timestamp default null, dt_fim_p timestamp default null) AS $body$
DECLARE


qt_refeicao_w	bigint;
vl_custo_real_w	double precision;
parameter124_w 	varchar(1);


BEGIN

parameter124_w := obter_parametro_funcao_padrao(1003, 124, nm_usuario_p);
if (nr_seq_cardapio_p IS NOT NULL AND nr_seq_cardapio_p::text <> '') then

	select	sum(o.vl_custo_real)
	into STRICT	vl_custo_real_w
	from    nut_rec_real_comp o,
		nut_receita_real r,
		nut_cardapio c,
		nut_servico s,
		nut_cardapio_dia d
	where   o.nr_seq_rec_real = r.nr_sequencia
	and     r.nr_seq_cardapio    = c.nr_sequencia
	and     c.nr_seq_card_dia = d.nr_sequencia
	and     s.nr_sequencia   = d.nr_seq_servico
	and     d.nr_sequencia = nr_seq_cardapio_p;

	if (parameter124_w = 'S') then
		select 	count(*)
		into STRICT	qt_refeicao_w
		from	nut_cardapio_dia a,
				nut_cardapio c, 
				nut_pac_opcao_rec b, 
				nut_atend_serv_dia e
		where 	a.nr_sequencia = c.nr_seq_card_dia
		and  	b.nr_seq_cardapio = c.nr_sequencia
		and  	e.nr_sequencia = b.nr_seq_servico_dia
		and (coalesce(dt_inicio_p::text, '') = '' or (e.dt_servico between trunc(dt_inicio_p) and fim_dia(dt_fim_p)))
		and		(b.dt_liberacao IS NOT NULL AND b.dt_liberacao::text <> '')
		and 	a.nr_sequencia = nr_seq_cardapio_p    
		and 	parameter124_w = 'S' 
		and 	(((nut_cor_card_ordem_prod(a.nr_sequencia) IS NOT NULL AND (nut_cor_card_ordem_prod(a.nr_sequencia))::text <> '')) 
				or (coalesce(nut_cor_card_ordem_prod(a.nr_sequencia)::text, '') = ''));
				
		if ( coalesce(vl_custo_real_w::text, '') = '' ) then
			vl_custo_real_w := nut_obter_valor_total(qt_refeicao_w ,nr_seq_cardapio_p,4);
		end if;
	
	else
		select	coalesce(qt_pessoa_atend,0)
		into STRICT	qt_refeicao_w
		from	nut_cardapio_dia
		where	nr_sequencia = nr_seq_cardapio_p;
		
		if ( coalesce(vl_custo_real_w::text, '') = '' ) then
			vl_custo_real_w := nut_obter_valor_total(0,nr_seq_cardapio_p,2);
		end if;

	end if;

	insert into nut_ordem_prod_card(
		nr_sequencia,
		nr_seq_ordem,
		dt_atualizacao,
		nm_usuario,
		dt_atualizacao_nrec,
		nm_usuario_nrec,
		nr_seq_cardapio,
		qt_refeicao,
		cd_material,
		vl_custo)
	values (	
		nextval('nut_ordem_prod_card_seq'),
		nr_seq_odem_p,
		clock_timestamp(),
		nm_usuario_p,
		clock_timestamp(),
		nm_usuario_p,
		nr_seq_cardapio_p,
		qt_refeicao_w,
		cd_material_p,
		vl_custo_real_w);
end if;

commit;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
