SET client_encoding TO 'UTF8';





CREATE OR REPLACE PROCEDURE inserir_card_descricao ( cd_material_p bigint, nm_usuario_p text) AS $body$
DECLARE




nr_sequencia_w				MEDIC_DESCRITIVO.NR_SEQUENCIA%TYPE;
nr_seq_ficha_tecnica_w			MEDIC_DESCRITIVO.NR_SEQ_FICHA_TECNICA%TYPE;
dt_atualizacao_w			MEDIC_DESCRITIVO.DT_ATUALIZACAO%TYPE;
nm_usuario_w				MEDIC_DESCRITIVO.NM_USUARIO%TYPE;
ie_tipo_w				MEDIC_DESCRITIVO.IE_TIPO%TYPE;
ds_descritivo_w				MEDIC_DESCRITIVO.DS_DESCRITIVO%TYPE;
dt_atualizacao_nrec_w			MEDIC_DESCRITIVO.DT_ATUALIZACAO_NREC%TYPE;
nm_usuario_nrec_w			MEDIC_DESCRITIVO.NM_USUARIO_NREC%TYPE;
nr_sequencia_dashboard_w		bigint;
ds_tipo_w				MATERIAL_TIPO_DESCR.DS_TIPO%TYPE;
nr_total				smallint;

c01 CURSOR FOR
SELECT b.NR_SEQUENCIA,
b.NR_SEQ_FICHA_TECNICA,
b.DT_ATUALIZACAO,
b.NM_USUARIO,
b.IE_TIPO,
b.DS_DESCRITIVO,
b.DT_ATUALIZACAO_NREC,
b.NM_USUARIO, 
a.DS_TIPO
from MEDIC_DESCRITIVO b,
material_tipo_descr a
where b.NR_SEQ_FICHA_TECNICA = (SELECT	MAX(NR_SEQ_FICHA_TECNICA) nr_seq_ficha_tecnica
FROM	material
WHERE	cd_material = cd_material_p)
and b.ie_tipo = a.cd_tipo
	      and b.nr_seq_ficha_tecnica = ( select max(x.nr_seq_ficha_tecnica)
					     from material x
					     where x.cd_material = cd_material_p)
order by a.ds_tipo;


BEGIN

delete from dashboard_cards where nr_seq_dashboard = 407;

open c01;
loop
fetch c01 into
	nr_sequencia_w,
	nr_seq_ficha_tecnica_w,
	dt_atualizacao_w,
	nm_usuario_w,
	ie_tipo_w,
	ds_descritivo_w,
	dt_atualizacao_nrec_w,
	nm_usuario_nrec_w,
	ds_tipo_w;
EXIT WHEN NOT FOUND; /* apply on c01 */
	begin
		
	      SELECT count(*)
	      into STRICT nr_total
	      FROM dashboard_base 
	      WHERE nr_sequencia = 407;
	
	      if (nr_total < 1)then
		INSERT INTO dashboard_base(NR_SEQUENCIA,DT_ATUALIZACAO, NM_USUARIO, DT_ATUALIZACAO_NREC, NM_USUARIO_NREC) VALUES (407,clock_timestamp(),nm_usuario_p,clock_timestamp(),nm_usuario_p);
		commit;
	      end if;
	
	      
	      SELECT count(*) 
	      into STRICT nr_total
	      FROM ind_base 
	      WHERE nr_sequencia = 257;
	
	      if (nr_total < 1)then
		INSERT INTO ind_base(nr_sequencia, dt_atualizacao, nm_usuario, dt_atualizacao_nrec, nm_usuario_nrec, ds_indicador, nr_seq_ordem_serv, ie_padrao_sistema, ds_sql_where, ds_origem_inf, cd_exp_indicador, ds_objetivo, ie_situacao)
			             values (257, clock_timestamp(), nm_usuario_p, clock_timestamp(), nm_usuario_p, 'Agenda Exames', null, 'S', 'and', 'Eis_Agenda_v', 320180, '', 'A');
		commit;
	      end if;
	
	      
	      SELECT count(*) 
	      into STRICT nr_total
	      FROM ind_base 
	      WHERE nr_sequencia = 268;
	
	      if (nr_total < 1)then
		INSERT INTO ind_base(nr_sequencia, dt_atualizacao, nm_usuario, dt_atualizacao_nrec, nm_usuario_nrec, ds_indicador, nr_seq_ordem_serv, ie_padrao_sistema, ds_sql_where, ds_origem_inf, cd_exp_indicador, ds_objetivo, ie_situacao)
			             values (268, clock_timestamp(), nm_usuario_p, clock_timestamp(), nm_usuario_p, 'Alergia', null, 'S', 'and', 'Eis_Alergia_v', 283343, '', 'A');
		commit;
	      end if;
	
	      
	      SELECT count(*) 
	      into STRICT nr_total
	      FROM ind_informacao 
	      WHERE nr_sequencia = 611;
	
	      if (nr_total < 1)then
		INSERT INTO ind_informacao(nr_sequencia, dt_atualizacao, nm_usuario, dt_atualizacao_nrec, nm_usuario_nrec, ds_informacao, nr_seq_indicador, ds_atributo, cd_exp_informacao, ds_objetivo) values (611, clock_timestamp(), nm_usuario_p, clock_timestamp(), nm_usuario_p, 'Agendas', 257, 'QT_AGENDA', 283249, '');
		commit;
	      end if;
	
	      
	      SELECT count(*)
	      into STRICT nr_total
	      FROM ind_dimensao 
	      WHERE nr_sequencia = 1000;
	
	      if (nr_total < 1)then 
		insert into ind_dimensao(nr_sequencia, dt_atualizacao, nm_usuario, dt_atualizacao_nrec, nm_usuario_nrec, ds_dimensao, nr_seq_indicador, ds_atributo, ds_sql_where, cd_exp_dimensao, ds_objetivo, nr_seq_dim_drill)
			                values (1000, clock_timestamp(), nm_usuario_p, clock_timestamp(), nm_usuario_p, 'Faixa etÃ¡ria', 268, 'IE_FAIXA_ETARIA', 'and', 289965, '', null);
		commit;	 	
	      end if;
	
	      
	      select coalesce(MAX(nr_sequencia), 0) 
	      into STRICT nr_sequencia_dashboard_w
	      from  dashboard_cards;
	
	      insert into dashboard_cards(NR_SEQUENCIA, dt_atualizacao, nm_usuario, dt_atualizacao_nrec, nm_usuario_nrec, nr_seq_dashboard, nr_seq_indicator, nr_seq_dimension, nr_seq_information, ds_title,   qt_colspan, qt_rowspan, ie_chart_type, ie_load_from_bi)
	      values (nr_sequencia_dashboard_w + 1, clock_timestamp(), nm_usuario_p, clock_timestamp(), nm_usuario_p, 407, 257, 1000, 611, ds_tipo_w, 1, 1, 'pie', 'N');
	end;		
end loop;
close c01;
commit;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
