CREATE OR REPLACE PROCEDURE inserir_cheque_rec_cobranca ( nr_seq_cheque_p bigint, nr_seq_historico_p bigint, nr_seq_cobrador_p bigint, cd_estabelecimento_p bigint, nm_usuario_p text) AS $body$
DECLARE


vl_cheque_w		double precision;
nr_seq_cobranca_w	bigint;


BEGIN
select	max(vl_cheque)
into STRICT	vl_cheque_w
from	cheque_cr
where	nr_seq_cheque	= nr_seq_cheque_p;

select	nextval('cobranca_seq')
into STRICT	nr_seq_cobranca_w
;

insert into cobranca(nr_sequencia,
	nm_usuario,
	dt_atualizacao,
	nm_usuario_nrec,
	dt_atualizacao_nrec,
	cd_estabelecimento,
	dt_inclusao,
	dt_previsao_cobranca,
	ie_status,
	nr_seq_cheque,
	vl_acobrar,
	vl_original,
	nr_seq_cobrador)
values (nr_seq_cobranca_w,
	nm_usuario_p,
	clock_timestamp(),
	nm_usuario_p,
	clock_timestamp(),
	cd_estabelecimento_p,
	clock_timestamp(),
	clock_timestamp(),
	'P',
	nr_seq_cheque_p,
	vl_cheque_w,
	vl_cheque_w,
	nr_seq_cobrador_p);

if (nr_seq_cobrador_p IS NOT NULL AND nr_seq_cobrador_p::text <> '') then
	insert into cobranca_alt_cobrador(nr_sequencia,
		nm_usuario,
		dt_atualizacao,
		nm_usuario_nrec,
		dt_atualizacao_nrec,
		dt_alteracao,
		nr_seq_cobr_novo,
		nr_seq_cobranca)
	values (nextval('cobranca_alt_cobrador_seq'),
		nm_usuario_p,
		clock_timestamp(),
		nm_usuario_p,
		clock_timestamp(),
		clock_timestamp(),
		nr_seq_cobrador_p,
		nr_seq_cobranca_w);
end if;

if (somente_numero(nr_seq_historico_p) <> 0) then
	insert into cobranca_historico(nr_sequencia,
		nm_usuario,
		dt_atualizacao,
		nm_usuario_nrec,
		dt_atualizacao_nrec,
		dt_historico,
		nr_seq_cobranca,
		nr_seq_historico)
	values (nextval('cobranca_historico_seq'),
		nm_usuario_p,
		clock_timestamp(),
		nm_usuario_p,
		clock_timestamp(),
		clock_timestamp(),
		nr_seq_cobranca_w,
		somente_numero(nr_seq_historico_p));
end if;

commit;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
