CREATE OR REPLACE PROCEDURE inserir_classif_agenda_padrao (cd_pessoa_fisica_p text, nm_usuario_p text) AS $body$
DECLARE



cd_classif_agenda_w			varchar(01);
ds_classif_agenda_w			varchar(50);
nr_sequencia_w				bigint;


c01 CURSOR FOR
	SELECT	a.cd_classif_agenda,
		a.ds_classif_agenda
	from	classif_agenda_consulta_v a
	where	a.cd_classif_agenda in ('N', 'E', 'O', 'A')
	and	not exists (SELECT 1
		from	med_classif_agenda x
		where	x.cd_medico		= cd_pessoa_fisica_p
		and	x.ie_classif_agenda	= a.cd_classif_agenda);


BEGIN

open	c01;
loop
fetch	c01 into
	cd_classif_agenda_w,
	ds_classif_agenda_w;
EXIT WHEN NOT FOUND; /* apply on c01 */
	begin

	select	nextval('med_classif_agenda_seq')
	into STRICT	nr_sequencia_w
	;


	insert into med_classif_agenda(
		nr_sequencia,
		cd_medico,
		ie_classif_agenda,
		ds_classif_agenda,
		dt_atualizacao,
		nm_usuario,
		ds_cor_fonte,
		ds_cor_fundo,
		nr_minuto_duracao,
		ie_ficha_paciente,
		ie_tipo)
	values (nr_sequencia_w,
		cd_pessoa_fisica_p,
		cd_classif_agenda_w,
		ds_classif_agenda_w,
		clock_timestamp(),
		nm_usuario_p,
		'clNavy',
		'clWindow',
		30,
		'N',
		'C');
	end;
end	loop;
close	c01;


commit;
end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
