SET client_encoding TO 'UTF8';





CREATE OR REPLACE PROCEDURE inserir_classif_diagnostico ( ds_tipo_p text, cd_estabelecimento_p bigint, nm_usuario_p text, nr_atendimento_p bigint, cd_pessoa_fisica_p text, cd_doenca_p text, dt_diagnostico_p timestamp, ie_status_diag_p text default null) AS $body$
DECLARE


ds_valor_w			varchar(20);
ds_valor_aux_w			varchar(255);
ds_tipo_w			varchar(255);
pos_w				bigint;
nr_seq_classif_w 			bigint;
nr_seq_regra_conv_w 		bigint;
ie_diag_tratamento_w 		diagnostico_doenca.ie_diag_tratamento%type;
ie_diag_princ_depart_w 		diagnostico_doenca.ie_diag_princ_depart%type;
ie_diag_admissao_w 		diagnostico_doenca.ie_diag_admissao%type;
ie_diag_princ_episodio_w 		diagnostico_doenca.ie_diag_princ_episodio%type;
ie_diag_alta_w 			diagnostico_doenca.ie_diag_alta%type;
ie_diag_cirurgia_w 			diagnostico_doenca.ie_diag_cirurgia%type;
ie_diag_referencia_w 		diagnostico_doenca.ie_diag_referencia%type;
ie_diag_obito_w 			diagnostico_doenca.ie_diag_obito%type;
ie_diag_pre_cir_w 			diagnostico_doenca.ie_diag_pre_cir%type;
ie_diag_trat_cert_w 		diagnostico_doenca.ie_diag_trat_cert%type;
ie_status_diag_w 			diagnostico_doenca.ie_status_diag%type;
ie_diag_cronico_w 			diagnostico_doenca.ie_diag_cronico%type;
ie_diag_trat_especial_w 		diagnostico_doenca.ie_diag_trat_especial%type;


BEGIN

ie_diag_trat_especial_w 	:= obter_marcar_classif('IE_DIAG_TRAT_ESPECIAL');
ie_diag_cronico_w 		:= obter_marcar_classif('IE_DIAG_CRONICO');
ie_diag_referencia_w 	:= obter_marcar_classif('IE_DIAG_REFERENCIA');
ie_diag_tratamento_w 	:= obter_marcar_classif('IE_DIAG_TRATAMENTO');
ie_diag_admissao_w 	:= obter_marcar_classif('IE_DIAG_ADMISSAO');
ie_diag_trat_cert_w 	:= obter_marcar_classif('IE_DIAG_TRAT_CERT');
ie_diag_pre_cir_w 		:= obter_marcar_classif('IE_DIAG_PRE_CIR');
ie_diag_cirurgia_w 		:= obter_marcar_classif('IE_DIAG_CIRURGIA');
ie_diag_alta_w 		:= obter_marcar_classif('IE_DIAG_ALTA');
ie_diag_obito_w 		:= obter_marcar_classif('IE_DIAG_OBITO');
ie_diag_princ_depart_w 	:= obter_marcar_classif('IE_DIAG_PRINC_DEPART');
ie_diag_princ_episodio_w 	:= obter_marcar_classif('IE_DIAG_PRINC_EPISODIO');


if ((trim(both ds_tipo_p) IS NOT NULL AND (trim(both ds_tipo_p))::text <> '')) then
	ds_tipo_w	:= ds_tipo_p||';';
	ds_tipo_w := replace(replace(ds_tipo_w,'"',''),'$','');
	pos_w := position(';' in ds_tipo_w);

	while pos_w > 0 loop

		select	replace(substr( ds_tipo_w, 1, pos_w ),';','')
		into STRICT	ds_valor_w
		;

		select	substr( ds_tipo_w, pos_w +1 , 999 )
		into STRICT	ds_tipo_w
		;

		pos_w := position(';' in ds_tipo_w);
		
		if (ds_valor_w = 'BD') then
			ie_diag_tratamento_w := 'S';
		elsif (ds_valor_w = 'FHD') then
			ie_diag_princ_depart_w := 'S';
		elsif (ds_valor_w = 'AD') then
			ie_diag_admissao_w := 'S';
		elsif (ds_valor_w = 'KHD') then
			ie_diag_princ_episodio_w := 'S';
		elsif (ds_valor_w = 'ED') then
			ie_diag_alta_w := 'S';
		elsif (ds_valor_w = 'OD') then
			ie_diag_cirurgia_w := 'S';
		elsif (ds_valor_w = 'UED') then
			ie_diag_referencia_w := 'S';
		end if;

	end loop;
	
end if;

update 	diagnostico_doenca
set 	dt_atualizacao 		= clock_timestamp(),
	nm_usuario		= nm_usuario_p,
	ie_diag_tratamento 		= ie_diag_tratamento_w,
	ie_diag_princ_depart 	= ie_diag_princ_depart_w,
	ie_diag_admissao 		= ie_diag_admissao_w,
	ie_diag_princ_episodio 	= ie_diag_princ_episodio_w,
	ie_diag_alta 		= ie_diag_alta_w,
	ie_diag_cirurgia 		= ie_diag_cirurgia_w,
	ie_diag_referencia 		= ie_diag_referencia_w,
	ie_diag_obito 		= ie_diag_obito_w,
	ie_diag_pre_cir 		= ie_diag_pre_cir_w,
	ie_diag_trat_cert 		= ie_diag_trat_cert_w,
	ie_diag_cronico 		= ie_diag_cronico_w,
	ie_diag_trat_especial 	= ie_diag_trat_especial_w,
	ie_status_diag  	= ie_status_diag_p
where	nr_atendimento = nr_atendimento_p
and 	cd_doenca = cd_doenca_p
and	dt_diagnostico = dt_diagnostico_p;

commit;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
