SET client_encoding TO 'UTF8';





CREATE OR REPLACE PROCEDURE inserir_cliente_abc_gerencia ( nr_seq_gerencia_p bigint, ie_tipo_p text, lista_informacao_p text, nm_usuario_p text) AS $body$
DECLARE


lista_informacao_w		varchar(400);
ie_contador_w			bigint	:= 0;
tam_lista_w			bigint;
ie_pos_virgula_w		smallint;
nr_seq_cliente_w			bigint;


BEGIN

lista_informacao_w	:= lista_informacao_p;

WHILE	(lista_informacao_w IS NOT NULL AND lista_informacao_w::text <> '') OR
	ie_contador_w > 200 LOOP
	BEGIN
	tam_lista_w		:= LENGTH(lista_informacao_w);
	ie_pos_virgula_w	:= position(',' in lista_informacao_w);

	IF (ie_pos_virgula_w <> 0) THEN
		nr_seq_cliente_w		:= SUBSTR(lista_informacao_w,1,(ie_pos_virgula_w - 1));
		lista_informacao_w	:= SUBSTR(lista_informacao_w,(ie_pos_virgula_w + 1),tam_lista_w);
	END IF;

	INSERT INTO gerencia_abc_cliente(
		nr_sequencia,
		nr_seq_gerencia,
		nr_seq_cliente,
		dt_atualizacao,
		nm_usuario,
		dt_atualizacao_nrec,
		nm_usuario_nrec,
		IE_AVALIACAO
	) VALUES (
		nextval('gerencia_abc_cliente_seq'),
		nr_seq_gerencia_p,
		nr_seq_cliente_w,
		clock_timestamp(),
		nm_usuario_p,
		clock_timestamp(),
		nm_usuario_p,
		ie_tipo_p
	);

	ie_contador_w	:= ie_contador_w + 1;

	END;
END LOOP;

END;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
