CREATE OR REPLACE PROCEDURE inserir_cod_barras_auto ( nm_usuario_p text) AS $body$
DECLARE


cd_barras_w		numeric(38);


BEGIN
select (max(obter_somente_numero(a.cd_barras)) + 1)
into STRICT	cd_barras_w
from	usuario a
where	(a.cd_barras IS NOT NULL AND a.cd_barras::text <> '');

if (coalesce(cd_barras_w::text, '') = '') then
	cd_barras_w := 1;
end if;

if (cd_barras_w IS NOT NULL AND cd_barras_w::text <> '') then
	update	usuario
	set	cd_barras = to_char(cd_barras_w)
	where	nm_usuario = nm_usuario_p
	and	coalesce(cd_barras::text, '') = '';
end if;
commit;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
