CREATE OR REPLACE PROCEDURE inserir_contas_gestao_doc ( nr_seq_gestao_doc_p bigint, nr_seq_protocolo_p bigint, nr_interno_conta_p bigint, nm_usuario_p text) AS $body$
DECLARE

					 
nr_seq_interno_conta_w			conta_paciente.nr_interno_conta%type;
qt_conta_w				bigint;

C01 CURSOR FOR 
	SELECT	a.nr_interno_conta 
	from	conta_paciente a 
	where	a.nr_seq_protocolo =	nr_seq_protocolo_p 
	and	not exists (SELECT	1 
				from	gestao_doc_item x 
				where	x.nr_seq_interno	= a.nr_interno_conta) 
	order by 
		a.nr_interno_conta;


BEGIN 
if (coalesce(nr_interno_conta_p,0) > 0) then 
	select	count(1) 
	into STRICT	qt_conta_w 
	from	gestao_doc_item	a 
	where	a.nr_seq_interno	= nr_interno_conta_p  LIMIT 1;
	 
	if (qt_conta_w = 0) then 
		insert into gestao_doc_item(nr_sequencia, 
			nr_seq_interno, 
			nr_seq_gestao_doc, 
			nm_usuario, 
			ie_digitalizado, 
			dt_atualizacao) 
		values (nextval('gestao_doc_item_seq'), 
			nr_interno_conta_p, 
			nr_seq_gestao_doc_p, 
			nm_usuario_p, 
			'N', 
			clock_timestamp());
	end if;
elsif (coalesce(nr_seq_protocolo_p,0) > 0) then 
	open C01;
	loop 
	fetch C01 into	 
		nr_seq_interno_conta_w;
	EXIT WHEN NOT FOUND; /* apply on C01 */
		begin 
		insert into gestao_doc_item(nr_sequencia, 
			nr_seq_interno, 
			nr_seq_gestao_doc, 
			nm_usuario, 
			ie_digitalizado, 
			dt_atualizacao) 
		values (nextval('gestao_doc_item_seq'), 
			nr_seq_interno_conta_w, 
			nr_seq_gestao_doc_p, 
			nm_usuario_p, 
			'N', 
			clock_timestamp());
		end;
	end loop;
	close C01;
end if;
 
commit;
 
end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
