CREATE OR REPLACE PROCEDURE inserir_conta_cancel_protoc_js ( ie_cancelamento_p text, nr_interno_conta_p bigint, nr_seq_conta_origem_p bigint, nr_seq_protocolo_p bigint, nm_usuario_p text) AS $body$
DECLARE

 
qt_fatur_etapa_w	bigint;
				

BEGIN 
 
if (ie_cancelamento_p = 'E')then 
	begin 
	 
	if (nr_interno_conta_p IS NOT NULL AND nr_interno_conta_p::text <> '')then 
		begin 
		 
		update  conta_paciente 
		set    dt_atualizacao 	= clock_timestamp(), 
			 nm_usuario   	= nm_usuario_p, 
			 nr_seq_protocolo 	= nr_seq_protocolo_p 
		where   nr_interno_conta 	= nr_interno_conta_p 
		and	   coalesce(nr_seq_protocolo::text, '') = '';
		 
		select 	count(*) 
		into STRICT 	qt_fatur_etapa_w 
		from 	fatur_etapa_alta 
		where 	ie_evento = 'B';
		 
		if (qt_fatur_etapa_w > 0)then 
			begin 
			 
			CALL gerar_etapa_inserir_protocolo(nr_interno_conta_p, nm_usuario_p);
			 
			end;
		end if;
		 
		end;
	end if;
	 
	end;
end if;
 
if (ie_cancelamento_p = 'C')then 
	begin 
	 
	if (nr_interno_conta_p IS NOT NULL AND nr_interno_conta_p::text <> '')then 
		begin 
		 
		update  conta_paciente 
		set    dt_atualizacao 	= clock_timestamp(), 
			 nm_usuario   	= nm_usuario_p, 
			 nr_seq_protocolo 	= nr_seq_protocolo_p 
		where   nr_seq_conta_origem 	= nr_seq_conta_origem_p 
		and    ie_cancelamento 	= 'E' 
		and   coalesce(nr_seq_protocolo::text, '') = '';
		 
		select 	count(*) 
		into STRICT 	qt_fatur_etapa_w 
		from 	fatur_etapa_alta 
		where 	ie_evento = 'B';
		 
		if (qt_fatur_etapa_w > 0)then 
			begin 
			 
			CALL gerar_etapa_inserir_protocolo(nr_interno_conta_p, nm_usuario_p);
			 
			end;
		end if;
		 
		end;
	end if;
	 
	end;
end if;
 
commit;
 
end;
$body$
