CREATE OR REPLACE PROCEDURE inserir_conta_contab_man ( nr_titulo_p bigint, cd_cgc_p text, nr_seq_classe_cp_p bigint) AS $body$
DECLARE


cd_conta_contabil_w	conta_contabil.cd_conta_contabil%type;


BEGIN
if (nr_titulo_p IS NOT NULL AND nr_titulo_p::text <> '') then
	select	max(a.cd_conta_contabil)
	into STRICT	cd_conta_contabil_w
	from	pessoa_jur_conta_nf a
	where	a.cd_cgc	= cd_cgc_p
	and	a.nr_seq_classe_cp = nr_seq_classe_cp_p;

	if (cd_conta_contabil_w IS NOT NULL AND cd_conta_contabil_w::text <> '') then
		update	titulo_pagar
		set	cd_conta_contabil	= cd_conta_contabil_w
		where	nr_titulo		= nr_titulo_p;

		commit;

	end if;
end if;

end;
