SET client_encoding TO 'UTF8';





CREATE OR REPLACE PROCEDURE inserir_conta_estornada_prot ( nr_seq_prot_dest_p bigint, nr_interno_conta_p bigint, nm_usuario_p text) AS $body$
DECLARE

 
nr_interno_conta_est_w		bigint;
nr_protocolo_w			varchar(40) := '';
		

BEGIN 
 
begin 
select	nr_protocolo 
into STRICT	nr_protocolo_w 
from	protocolo_convenio 
where	nr_seq_protocolo = nr_seq_prot_dest_p;
exception 
when others then 
	nr_protocolo_w := '';
end;
 
select	coalesce(max(nr_interno_conta),0) 
into STRICT	nr_interno_conta_est_w 
from	conta_paciente 
where	nr_seq_conta_origem = nr_interno_conta_p 
and	ie_cancelamento = 'E';
 
if (nr_interno_conta_est_w <> 0) and (coalesce(nr_protocolo_w,'X') <> 'X')then 
 
	update 	conta_paciente 
	set 	nr_seq_protocolo	= nr_seq_prot_dest_p, 
		nr_protocolo		= nr_protocolo_w, 
		dt_atualizacao		= clock_timestamp(), 
		nm_usuario		= nm_usuario_p 
	where 	nr_interno_conta 	= nr_interno_conta_est_w;	
				 
end if;
 
commit;
 
end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
