	SELECT	nr_interno_conta 
	from	pessoa_fisica c, 
		atendimento_paciente b, 
		conta_paciente a 
	where	a.nr_atendimento = b.nr_atendimento 
	and	b.cd_pessoa_fisica = c.cd_pessoa_fisica 
