open C01;
loop 
fetch C01 into	 
	nr_interno_conta_w;
EXIT WHEN NOT FOUND; /* apply on C01 */
	begin 
	vl_adicional := Inserir_Conta_Etapa(nr_interno_conta_w, nm_usuario_p, nr_seq_etapa_p, cd_setor_atend_p, null, cd_pessoa_fisica_w, null, null, null, vl_adicional);	
	end;
end loop;
close C01;
 
end;
