SET client_encoding TO 'UTF8';





CREATE OR REPLACE PROCEDURE inserir_conta_pac_mat_proc ( nr_interno_conta_p material_atend_paciente.nr_interno_conta%type, nm_usuario_p text) AS $body$
DECLARE


nr_seq_conta_conv_w	conta_pac_deducao_conv.nr_sequencia%type;
dt_atualizacao_w	timestamp := clock_timestamp();

type nr_seq_procpaci_tp is table of procedimento_paciente.nr_sequencia%type index by integer;
type cd_procedimento_tp is table of procedimento_paciente.cd_procedimento%type index by integer;
type ie_origem_proced_tp is table of procedimento_paciente.ie_origem_proced%type index by integer;
type nr_seq_proc_interno_tp is table of procedimento_paciente.nr_seq_proc_interno%type index by integer;
type nr_seq_exame_tp is table of procedimento_paciente.nr_seq_exame%type index by integer;
type vl_procedimento_tp is table of procedimento_paciente.vl_procedimento%type index by integer;
type nr_seq_matpaci_tp is table of material_atend_paciente.nr_sequencia%type index by integer;
type cd_material_tp is table of material_atend_paciente.cd_material%type index by integer;
type vl_material_tp is table of material_atend_paciente.vl_material%type index by integer;

nr_seq_procpaci_w		nr_seq_procpaci_tp;
cd_procedimento_w		cd_procedimento_tp;
ie_origem_proced_w		ie_origem_proced_tp;
nr_seq_proc_interno_w		nr_seq_proc_interno_tp;
nr_seq_exame_w			nr_seq_exame_tp;
vl_procedimento_w		vl_procedimento_tp;
nr_seq_matpaci_w		nr_seq_matpaci_tp;
cd_material_w			cd_material_tp;
vl_material_w			vl_material_tp;

c00 CURSOR FOR
	SELECT	p.nr_sequencia,
		p.cd_procedimento,
		p.ie_origem_proced,
		p.nr_seq_proc_interno,
		p.nr_seq_exame,
		coalesce(p.vl_procedimento,0) vl_procedimento
	from 	procedimento_paciente p
	where 	p.nr_interno_conta = nr_interno_conta_p
	and     p.nr_sequencia not in (	SELECT	a.nr_seq_propaci_origem
					from	conta_pac_ded_conv_item a, 
						conta_pac_deducao_conv b
					where	a.nr_seq_deducao_conv = b.nr_sequencia 
					and	b.nr_seq_conta_orig = nr_interno_conta_p
					and	(a.nr_seq_propaci_origem IS NOT NULL AND a.nr_seq_propaci_origem::text <> ''))
	and 	p.nr_sequencia not in (	select	a.nr_seq_proc_pac
					from	conta_pac_deducao_proc a, 
						conta_pac_deducao_conv b
					where	a.nr_seq_conta_ded = b.nr_sequencia
					and	b.nr_seq_conta_orig = nr_interno_conta_p)
	and	not exists (	select	1
				from	conta_pac_deducao_proc x
				where	x.nr_seq_conta_ded = nr_seq_conta_conv_w
				and	x.nr_seq_proc_pac = p.nr_sequencia);

c01 CURSOR FOR
	SELECT	nr_sequencia,
		cd_material,
		vl_material
	from 	material_atend_paciente m
	where 	m.nr_interno_conta = nr_interno_conta_p
	and     m.nr_sequencia not in (	SELECT	a.nr_seq_matpaci_origem
					from 	conta_pac_ded_conv_item a, 
						conta_pac_deducao_conv b
					where 	a.nr_seq_deducao_conv = b.nr_sequencia 
					and   	b.nr_seq_conta_orig = nr_interno_conta_p
					and     (a.nr_seq_matpaci_origem IS NOT NULL AND a.nr_seq_matpaci_origem::text <> ''))
	and 	m.nr_sequencia not in (	select	a.nr_seq_mat_pac
					from	conta_pac_deducao_mat a, 
						conta_pac_deducao_conv b
					where	a.nr_seq_conta_ded = b.nr_sequencia
					and	b.nr_seq_conta_orig = nr_interno_conta_p)
	and	not exists (	select	1
				from	conta_pac_deducao_mat x
				where 	x.nr_seq_conta_ded = nr_seq_conta_conv_w
				and	x.nr_seq_mat_pac = m.nr_sequencia);


BEGIN

select	max(nr_sequencia)
into STRICT	nr_seq_conta_conv_w
from	conta_pac_deducao_conv
where	nr_seq_conta_orig = nr_interno_conta_p;

open c00;
loop
fetch c00 bulk collect into
	nr_seq_procpaci_w,
	cd_procedimento_w,
	ie_origem_proced_w,
	nr_seq_proc_interno_w,
	nr_seq_exame_w,
	vl_procedimento_w limit 1000;
exit when nr_seq_procpaci_w.count = 0;
	begin
	
	forall i in nr_seq_procpaci_w.first..nr_seq_procpaci_w.last
		insert into conta_pac_deducao_proc(
			nr_sequencia,
			nr_seq_conta_ded,
			nr_seq_proc_pac,
			cd_procedimento,
			ie_origem_proced,
			nr_seq_proc_interno,
			nr_seq_exame,
			pr_informado_calculo,
			dt_atualizacao,
			nm_usuario,
			dt_atualizacao_nrec,
			nm_usuario_nrec,
			vl_informado_calculo,
			vl_base_procedimento
		) values (
			nextval('conta_pac_deducao_proc_seq'),
			nr_seq_conta_conv_w,
			nr_seq_procpaci_w(i),
			cd_procedimento_w(i),
			ie_origem_proced_w(i),
			nr_seq_proc_interno_w(i),
			nr_seq_exame_w(i),
			0,
			dt_atualizacao_w,
			nm_usuario_p,
			dt_atualizacao_w,
			nm_usuario_p,
			0,
			vl_procedimento_w(i)
		);
	
	nr_seq_procpaci_w.delete;
	cd_procedimento_w.delete;
	ie_origem_proced_w.delete;
	nr_seq_proc_interno_w.delete;
	nr_seq_exame_w.delete;
	vl_procedimento_w.delete;	
	end;
end loop;
close c00;

open c01;
loop
fetch c01 bulk collect into
	nr_seq_matpaci_w,
	cd_material_w,
	vl_material_w limit 1000;
exit when nr_seq_matpaci_w.count = 0;
	begin
	
	forall i in nr_seq_matpaci_w.first..nr_seq_matpaci_w.last
		insert into conta_pac_deducao_mat(
			nr_sequencia,
			nr_seq_conta_ded,
			nr_seq_mat_pac,
			cd_material,
			pr_informado_calculo,
			dt_atualizacao,
			nm_usuario,
			dt_atualizacao_nrec,
			nm_usuario_nrec,
			vl_informado_calculo,
			vl_base_material
		) values (
			nextval('conta_pac_deducao_mat_seq'),
			nr_seq_conta_conv_w,
			nr_seq_matpaci_w(i),
			cd_material_w(i),
			0,
			dt_atualizacao_w,
			nm_usuario_p,
			dt_atualizacao_w,
			nm_usuario_p,
			0,
			vl_material_w(i)
		);
		
	nr_seq_matpaci_w.delete;
	cd_material_w.delete;
	vl_material_w.delete;
	end;
end loop;
close c01;

commit;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
-- REVOKE ALL ON PROCEDURE inserir_conta_pac_mat_proc ( nr_interno_conta_p material_atend_paciente.nr_interno_conta%type, nm_usuario_p text) FROM PUBLIC;

