CREATE OR REPLACE PROCEDURE inserir_conta_selec_prot_nf ( nr_interno_conta_p bigint, nr_seq_protocolo_p bigint, ie_excluir_ant_p text, nm_usuario_p text) AS $body$
DECLARE


qt_existe_w		bigint;


BEGIN

if (coalesce(ie_excluir_ant_p,'N') = 'S') then
	begin

	delete
	from	w_contas_selecionadas_prot
	where	nm_usuario = nm_usuario_p;

	end;
end if;

select	count(*)
into STRICT	qt_existe_w
from	conta_paciente_nf
where	nr_interno_conta = nr_interno_conta_p;

insert into w_contas_selecionadas_prot(
	nr_interno_conta,
	nr_seq_protocolo,
	ie_possui_nf,
	dt_atualizacao,
	nm_usuario)
values (
	nr_interno_conta_p,
	nr_seq_protocolo_p,
	CASE WHEN qt_existe_w=0 THEN 'N'  ELSE 'S' END ,
	clock_timestamp(),
	nm_usuario_p);

commit;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
