CREATE OR REPLACE PROCEDURE inserir_conversao_fornecedor ( nm_usuario_p text, cd_estabelecimento_p bigint, cd_material_p bigint, cd_cgc_p text, cd_unid_medida_p text, ie_consignado_p text, qt_conv_compra_est_p bigint) AS $body$
DECLARE


qt_conv_compra_est_w		double precision;
ie_consignado_w				material.ie_consignado%type;


BEGIN
ie_consignado_w := ie_consignado_p;

if (coalesce(ie_consignado_w::text, '') = '') then
	begin
	select	ie_consignado
	into STRICT	ie_consignado_w
	from	material
	where	cd_material = cd_material_p;
	exception
	when others then
		ie_consignado_w := ie_consignado_p;
	end;
end if;

select	coalesce(max(qt_conv_compra_est),0)
into STRICT	qt_conv_compra_est_w
from	material_fornec
where	cd_estabelecimento	= cd_estabelecimento_p
and	cd_material	= cd_material_p
and	cd_cgc		= cd_cgc_p
and	cd_unid_medida	= cd_unid_medida_p
and	ie_consignado	= ie_consignado_w;

if (qt_conv_compra_est_w = 0) then
	insert	into material_fornec(
		nr_sequencia,
		cd_estabelecimento,
		cd_material,
		dt_atualizacao,
		nm_usuario,
		cd_cgc,
		ie_consignado,
		cd_unid_medida,
		qt_conv_compra_est,
		ie_ressuprimento,
		ie_nota_fiscal,
		ie_cot_compra)
	values (	nextval('material_fornec_seq'),
		cd_estabelecimento_p,
		cd_material_p,
		clock_timestamp(),
		nm_usuario_p,
		cd_cgc_p,
		ie_consignado_w,
		cd_unid_medida_p,
		qt_conv_compra_est_p,
		'N',
		'S',
		'N');
else
	update	material_fornec
	set	qt_conv_compra_est	= qt_conv_compra_est_p,
		nm_usuario		= nm_usuario_p
	where	cd_estabelecimento		= cd_estabelecimento_p
	and	cd_material		= cd_material_p
	and	cd_cgc			= cd_cgc_p
	and	cd_unid_medida		= cd_unid_medida_p
	and	ie_consignado		= ie_consignado_w;
end if;

commit;

end;
$body$
