CREATE OR REPLACE PROCEDURE inserir_copia_tit_pagar ( nr_sequencia_p bigint, cd_empresa_destino_p bigint, cd_estab_destino_p bigint, cd_estab_origem_p bigint, nm_usuario_p text) AS $body$
DECLARE


nr_sequencia_w				classe_titulo_pagar.nr_sequencia%type;
nr_sequencia_ref_w	        classe_titulo_pagar.nr_sequencia_ref%type;
nr_seq_grupo_w				grupo_emp_estrutura.nr_seq_grupo%type;
cd_empresa_origem_w		    grupo_emp_estrutura.cd_empresa%type;
cd_empresa_controladora_w	grupo_emp_estrutura.cd_empresa%type	:= 0;


BEGIN

    cd_empresa_origem_w := obter_empresa_estab(cd_estab_origem_p);
    nr_seq_grupo_w := holding_pck.get_grupo_emp_usuario_vigente(cd_empresa_origem_w);

    select nextval('classe_titulo_pagar_seq')
    into STRICT   nr_sequencia_w
;

    if (coalesce(nr_seq_grupo_w, 0) <> 0) then
        cd_empresa_controladora_w := holding_pck.get_emp_controladora_grupo(nr_seq_grupo_w);
        if (cd_empresa_controladora_w <> cd_empresa_origem_w) then
            select	nr_sequencia_ref
            into STRICT	nr_sequencia_ref_w
            from	classe_titulo_pagar
            where	cd_empresa = cd_empresa_origem_w
            and 	nr_sequencia = nr_sequencia_p;
        else
            nr_sequencia_ref_w := nr_sequencia_p;
        end if;
    else
        nr_sequencia_ref_w := 0;
    end if;

    insert into classe_titulo_pagar(
        nr_sequencia,
        cd_estabelecimento,
        dt_atualizacao,
        nm_usuario,
        dt_atualizacao_nrec,
        nm_usuario_nrec,
        ds_classe,
        ie_situacao,
        nr_seq_trans_fin_baixa,
        nr_seq_trans_fin_contab,
        cd_empresa,
        ie_permite_bordero,
        nr_sequencia_ref)
    (SELECT
        nr_sequencia_w,
        CASE WHEN cd_estab_destino_p=0 THEN null  ELSE cd_estab_destino_p END ,
        clock_timestamp(),
        nm_usuario_p,
        clock_timestamp(),
        nm_usuario_p,
        ds_classe,
        ie_situacao,
        (select max(tf.nr_sequencia) From TRANSACAO_FINANCEIRA tf where tf.NR_SEQ_TRANS_FIN_REF = nr_seq_trans_fin_baixa and cd_empresa = cd_empresa_destino_p and cd_estabelecimento = cd_estab_destino_p),
        (select max(tf.nr_sequencia) From TRANSACAO_FINANCEIRA tf where tf.NR_SEQ_TRANS_FIN_REF = nr_seq_trans_fin_contab and cd_empresa = cd_empresa_destino_p and cd_estabelecimento = cd_estab_destino_p),
        cd_empresa_destino_p,
        ie_permite_bordero,
        CASE WHEN nr_sequencia_ref_w=0 THEN  null  ELSE nr_sequencia_ref_w END
     from  classe_titulo_pagar
     where nr_sequencia = nr_sequencia_p);

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
-- REVOKE ALL ON PROCEDURE inserir_copia_tit_pagar ( nr_sequencia_p bigint, cd_empresa_destino_p bigint, cd_estab_destino_p bigint, cd_estab_origem_p bigint, nm_usuario_p text) FROM PUBLIC;

