CREATE OR REPLACE PROCEDURE inserir_cot_compra_item_autor ( nr_seq_autorizacao_p bigint, nr_cot_compra_p bigint, nr_item_cot_compra_p bigint, nm_usuario_p text) AS $body$
DECLARE



qt_registros_w			bigint;
cd_material_w			material.cd_material%type;
qt_material_w			cot_compra_item.qt_material%type;
nr_seq_fabric_w			material.nr_seq_fabric%type;


BEGIN

select	cd_material,
	qt_material
into STRICT	cd_material_w,
	qt_material_w
from	cot_compra_item
where	nr_cot_compra = nr_cot_compra_p
and	nr_item_cot_compra = nr_item_cot_compra_p;

select	count(*)
into STRICT	qt_registros_w
from	material_autor_cirurgia
where	nr_seq_autorizacao = nr_seq_autorizacao_p
and	cd_material = cd_material_w;


if (qt_registros_w = 0) then

	select	nr_seq_fabric
	into STRICT	nr_seq_fabric_w
	from	material
        where	cd_material = cd_material_w;

	insert into material_autor_cirurgia(
		nr_sequencia,
		nr_seq_autorizacao,
		dt_atualizacao,
		nm_usuario,
		cd_material,
		qt_material,
		vl_material,
		ie_aprovacao,
		vl_unitario_material,
		dt_atualizacao_nrec,
		nm_usuario_nrec,
		qt_solicitada,
		nr_seq_fabricante,
		ie_valor_conta,
		ie_valor_informado,
		ie_gerado_agenda,
		cd_material_convenio,
		nr_cot_compra,
		nr_item_cot_compra,
		ie_reducao_acrescimo)
	values (	nextval('material_autor_cirurgia_seq'),
		nr_seq_autorizacao_p,
		clock_timestamp(),
		nm_usuario_p,
		cd_material_w,
		qt_material_w,
		0,
		'N',
		0,
		clock_timestamp(),
		nm_usuario_p,
		qt_material_w,
		nr_seq_fabric_w,
		'N',
		'N',
		'N',
		0,
		nr_cot_compra_p,
		nr_item_cot_compra_p,
		'A');
end if;

commit;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
