SET client_encoding TO 'UTF8';





CREATE OR REPLACE PROCEDURE inserir_dados_cirurgia_parto ( nr_cirurgia_p bigint) AS $body$
DECLARE



dt_entrada_unidade_w	timestamp;
cd_pessoa_fisica_w	varchar(10);
cd_medico_obstetra_w	varchar(10);
cd_medico_anestesista_w	varchar(10);
cd_medico_pediatra_w 	varchar(10);
ie_funcao_w		varchar(3);
nr_atendimento_w	bigint;
nm_medico_w	varchar(255);

ie_anestesista_w	varchar(1);
ie_obstetra_w		varchar(1);
ie_pediatra_w		varchar(1);

c01 CURSOR FOR
	SELECT	cd_pessoa_fisica,
		ie_funcao
	from	cirurgia_participante
	where	nr_cirurgia = nr_cirurgia_p
	and	coalesce(ie_situacao,'A') = 'A';


BEGIN

if (coalesce(nr_cirurgia_p,0)	> 0 ) then
	select	max(dt_entrada_unidade)
	into STRICT	dt_entrada_unidade_w
	from	cirurgia
	where	nr_cirurgia = nr_cirurgia_p;

	open c01;
	loop

	fetch c01 into	cd_pessoa_fisica_w,
			ie_funcao_w;
		EXIT WHEN NOT FOUND; /* apply on c01 */

		select	max(ie_anestesista),
			max(ie_obstetra),
			max(ie_pediatra)
		into STRICT	ie_anestesista_w,
			ie_obstetra_w,
			ie_pediatra_w
		from	funcao_medico
		where	cd_funcao = (ie_funcao_w)::numeric;

		if (ie_obstetra_w = 'S') then
			cd_medico_obstetra_w := cd_pessoa_fisica_w;
		elsif (ie_anestesista_w = 'S') then
			cd_medico_anestesista_w := cd_pessoa_fisica_w;
		elsif (ie_pediatra_w = 'S') then
			cd_medico_pediatra_w := cd_pessoa_fisica_w;
		end if;

	end loop;
	close c01;

	select	max(nr_atendimento)
	into STRICT	nr_atendimento_w
	from	cirurgia
	where	nr_cirurgia = nr_cirurgia_p;

	if	((cd_medico_obstetra_w IS NOT NULL AND cd_medico_obstetra_w::text <> '') and (Obter_se_medico(cd_medico_obstetra_w,'M') <> 'S')) then
		nm_medico_w := substr(obter_nome_pf(cd_medico_obstetra_w),1,255);
	elsif	((cd_medico_anestesista_w IS NOT NULL AND cd_medico_anestesista_w::text <> '') and (Obter_se_medico(cd_medico_anestesista_w,'M') <> 'S')) then
		nm_medico_w := substr(obter_nome_pf(cd_medico_anestesista_w),1,255);
	elsif	((cd_medico_pediatra_w IS NOT NULL AND cd_medico_pediatra_w::text <> '') and (Obter_se_medico(cd_medico_pediatra_w,'M') <> 'S')) then
		nm_medico_w := substr(obter_nome_pf(cd_medico_pediatra_w),1,255);
	end if;

	if (nm_medico_w IS NOT NULL AND nm_medico_w::text <> '') then
		CALL exibir_erro_abortar(wheb_mensagem_pck.get_texto(1030411,';NM_MEDICO_P='||nm_medico_w||';NR_CIRURGIA_P=' || nr_cirurgia_p),null);
	end if;

	update	parto
	set	dt_admissao	= dt_entrada_unidade_w,
		cd_medico	= cd_medico_obstetra_w,
		cd_anestesista	= cd_medico_anestesista_w,
		ie_local_nascimento	= '01'
	where	nr_atendimento	= nr_atendimento_w;

	update	nascimento
	set	cd_pediatra	= cd_medico_pediatra_w
	where	nr_atendimento	= nr_atendimento_w;

end if;

commit;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
-- REVOKE ALL ON PROCEDURE inserir_dados_cirurgia_parto ( nr_cirurgia_p bigint) FROM PUBLIC;

