SET client_encoding TO 'UTF8';





CREATE OR REPLACE PROCEDURE inserir_dev_mov_item ( nr_atendimento_p bigint, cd_setor_atendimento_p bigint, cd_material_p bigint, qt_material_p bigint, cd_motivo_devolucao_p bigint, nr_devolucao_p bigint, cd_pessoa_fisica_p bigint, nr_seq_lote_fornec_p text, nm_usuario_dev_p text, nm_usuario_p text) AS $body$
DECLARE


nr_sequencia_w 				farm_devolucao_mov_item.nr_sequencia%type;
cd_cgc_fornec_w				movimento_estoque.cd_fornecedor%type;
cd_local_estoque_w			movimento_estoque.cd_local_estoque%type;
cd_local_estoque_old_w		movimento_estoque.cd_local_estoque%type := 0;
cd_unidade_med_mov_w		movimento_estoque.cd_unidade_med_mov%type;
dt_atendimento_w			movimento_estoque.dt_movimento_estoque%type;
nr_seq_lote_fornec_w		movimento_estoque.nr_seq_lote_fornec%type;
qt_movimento_w				movimento_estoque.qt_movimento%type;
qt_estoque_w				movimento_estoque.qt_estoque%type;
qt_estoque_ww				movimento_estoque.qt_estoque%type;
qt_horas_dev_w				bigint := 0;
qt_saldo_mat_w				bigint := 0;
qt_material_w				bigint := 0;

c01 CURSOR FOR
	SELECT	max(a.cd_fornecedor),
			a.cd_local_estoque,
			max(a.dt_atendimento),
			a.nr_seq_lote_fornec,
			a.cd_unidade_med_mov,
			sum(qt_movimento)
	from	material_estoque_devolucao_v a
	where 	a.nr_atendimento       	= nr_atendimento_p
	and 	a.cd_setor_atendimento 	= cd_setor_atendimento_p
	and 	a.cd_material			= cd_material_p
	and 	coalesce(a.nr_seq_lote_fornec::text, '') = '' or (a.nr_seq_lote_fornec = nr_seq_lote_fornec_p)
	and 	(coalesce(qt_horas_dev_w,0) = 0 or ((a.dt_atendimento + (qt_horas_dev_w/24)) >= clock_timestamp()))
	group by
			a.cd_material,
			nr_seq_lote_fornec,
			a.cd_local_estoque,
			a.cd_unidade_med_mov,
			--trunc(dt_atendimento),
			ds_material
	having 	sum(a.qt_movimento) > 0;


BEGIN

qt_horas_dev_w := obter_param_usuario(42, 98, obter_perfil_ativo, nm_usuario_p, obter_estabelecimento_ativo, qt_horas_dev_w);
qt_material_w := qt_material_p;

open c01;
	loop
	fetch c01 into
	cd_cgc_fornec_w,
	cd_local_estoque_w,
	dt_atendimento_w,
	nr_seq_lote_fornec_w,
	cd_unidade_med_mov_w,
	qt_estoque_w;
EXIT WHEN NOT FOUND; /* apply on c01 */
	begin

	if (coalesce(cd_local_estoque_w,0) <> coalesce(cd_local_estoque_old_w,0)) and (qt_saldo_mat_w >= 0) then

		qt_saldo_mat_w := (qt_material_w - qt_estoque_w);

		if (qt_estoque_w < qt_material_w)then
			qt_material_w := qt_estoque_w;
		end if;
	end if;

	CALL gravar_log_tasy(1812, ' qt_estoque_ww=' || qt_estoque_ww || ' cd_local_estoque_w=' || cd_local_estoque_w || ' cd_local_estoque_old_w=' || cd_local_estoque_old_w ||
	' qt_material_w=' || qt_material_w || ' qt_saldo_mat_w=' || qt_saldo_mat_w || ' cd_setor_atendimento_p=' || cd_setor_atendimento_p ||
	' nr_seq_lote_fornec_w=' || nr_seq_lote_fornec_w || ' dt_atendimento_w=' || dt_atendimento_w, nr_atendimento_p);

	if (coalesce(qt_material_w,0) > 0)then

		select 	coalesce(max(nr_sequencia),0) + 1
		into STRICT 	nr_sequencia_w
		from 	farm_devolucao_mov_item;

		insert into farm_devolucao_mov_item(nr_sequencia,
				cd_cgc_fornec,
				cd_local_estoque,
				cd_material,
				cd_material_dev,
				cd_motivo_devolucao,
				cd_setor_atendimento,
				cd_unidade_medida,
				dt_atendimento,
				dt_atualizacao,
				nm_usuario,
				nm_usuario_devol,
				nr_prescricao,
				nr_seq_devolucao,
				nr_seq_lote,
				nr_seq_lote_fornec,
				nr_sequencia_prescricao,
				qt_material,
				qt_material_estoque,
				cd_pessoa_fisica)
		values (nr_sequencia_w,
				cd_cgc_fornec_w,
				cd_local_estoque_w,
				cd_material_p,
				cd_material_p,
				cd_motivo_devolucao_p,
				cd_setor_atendimento_p,
				cd_unidade_med_mov_w,
				dt_atendimento_w,
				clock_timestamp(),
				nm_usuario_p,
				nm_usuario_dev_p,
				null,
				nr_devolucao_p,
				null,
				nr_seq_lote_fornec_w,
				1,
				qt_material_w,
				qt_material_w,
				cd_pessoa_fisica_p);
	end if;

	qt_material_w := qt_saldo_mat_w;
	cd_local_estoque_old_w := cd_local_estoque_w;

	end;
end loop;
close c01;

commit;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
