CREATE OR REPLACE PROCEDURE inserir_diagnostico_relacao (nr_atendimento_p bigint, cd_doenca_p text, nm_usuario_p text, cd_doenca_superior_p text, dt_diagnostico_p timestamp, nr_cirurgia_p bigint default null, ie_lado_p text default null) AS $body$
DECLARE

  ie_existe_w bigint;

BEGIN
    SELECT Count(*)
    INTO STRICT   ie_existe_w 
    FROM   diagnostico_doenca 
    WHERE  cd_doenca = cd_doenca_p 
    AND (cd_doenca_superior = cd_doenca_superior_p or coalesce(cd_doenca_superior_p::text, '') = '')
    AND nr_atendimento = nr_atendimento_p
    AND dt_diagnostico = dt_diagnostico_p;

    IF ( ie_existe_w = 0 ) THEN 
      BEGIN 
          INSERT INTO diagnostico_doenca(nr_atendimento, 
                       dt_diagnostico, 
                       cd_doenca, 
                       dt_atualizacao, 
                       nm_usuario, 
                       ie_classificacao_doenca,
                       cd_doenca_superior,
                       nr_cirurgia,
                       ie_lado,
					   ie_situacao
                      ) 
          VALUES (nr_atendimento_p, 
                       dt_diagnostico_p, 
                       cd_doenca_p, 
                       clock_timestamp(), 
                       nm_usuario_p, 
                       'S', 
                       cd_doenca_superior_p,
                       nr_cirurgia_p,
                       ie_lado_p,
					   'A'
                      );

          COMMIT;
      END;
    END IF;
END;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
