SET client_encoding TO 'UTF8';





CREATE OR REPLACE PROCEDURE inserir_dispositivo_inf_adic ( nr_atendimento_p bigint,		-- Atendimento do paciente
 nr_seq_atend_disp_p bigint,		-- Sequencia da instalacao do dispositivo no atendimento
 nr_seq_regra_inf_p bigint,		-- Sequencia da regra para informacoes adicionais
 nr_seq_inf_adic_p bigint,		-- Sequencia do campo na regra
 ds_valor_p text,	-- Valor
 nm_usuario_p text, ie_acao_p text default null) AS $body$
	-- Usuario
DECLARE


nr_sequencia_w			bigint;
ie_tipo_campo_w			varchar(3);
nr_seq_apresentacao_w	dispositivo_inf_adic.nr_seq_apresentacao%TYPE;
ie_acao_w			varchar(1);


BEGIN

if (nr_atendimento_p IS NOT NULL AND nr_atendimento_p::text <> '') and (nr_seq_atend_disp_p IS NOT NULL AND nr_seq_atend_disp_p::text <> '') and (nr_seq_inf_adic_p IS NOT NULL AND nr_seq_inf_adic_p::text <> '') then

	select	max(a.ie_tipo_campo),
		max(a.nr_seq_apresentacao),
		max(b.ie_acao_evento)
	into STRICT	ie_tipo_campo_w,
		nr_seq_apresentacao_w,
		ie_acao_w
	from	picc_dispositivo_regra a,
		picc_dispositivo b
    	where	a.nr_sequencia = nr_seq_inf_adic_p
    	and	b.nr_sequencia = a.nr_seq_regra;
		
	select	coalesce(max(nr_sequencia),0)
	into STRICT	nr_sequencia_w
	from	dispositivo_inf_adic
	where	nr_seq_inf_adic = nr_seq_inf_adic_p
	and		nr_seq_atend_disp = nr_seq_atend_disp_p
	and		nr_atendimento = nr_atendimento_p
	and (coalesce(ie_acao::text, '') = '' or ie_acao = ie_acao_p);

	if (nr_sequencia_w = 0) then
	
		select	nextval('dispositivo_inf_adic_seq')
		into STRICT	nr_sequencia_w
		;

		insert into dispositivo_inf_adic(
				nr_sequencia,
				nr_atendimento,
				nr_seq_atend_disp,
				nr_seq_inf_adic,
				nr_inf_adic,
				ds_inf_adic,
				dt_inf_adic,
				dt_atualizacao,
				nm_usuario,
				dt_atualizacao_nrec,
				nm_usuario_nrec,
				nr_seq_apresentacao,
				ie_acao
			) values (
				nr_sequencia_w,
				nr_atendimento_p,
				nr_seq_atend_disp_p,
				nr_seq_inf_adic_p,
				CASE WHEN ie_tipo_campo_w='N' THEN (ds_valor_p)::numeric   ELSE null END ,
				CASE WHEN ie_tipo_campo_w='A' THEN substr(ds_valor_p,1,255)  ELSE null END ,
				CASE WHEN ie_tipo_campo_w='D' THEN to_date(ds_valor_p,'dd/mm/yyyy hh24:mi:ss')  ELSE null END ,
				clock_timestamp(),
				nm_usuario_p,
				clock_timestamp(),
				nm_usuario_p,
				nr_seq_apresentacao_w,
				ie_acao_w);

	else

		update	dispositivo_inf_adic
		set		nr_inf_adic = CASE WHEN ie_tipo_campo_w='N' THEN (ds_valor_p)::numeric   ELSE null END ,
				ds_inf_adic = CASE WHEN ie_tipo_campo_w='A' THEN substr(ds_valor_p,1,255)  ELSE null END ,
				dt_inf_adic = CASE WHEN ie_tipo_campo_w='D' THEN to_date(ds_valor_p,'dd/mm/yyyy hh24:mi:ss')  ELSE null END
		where	nr_seq_atend_disp = nr_seq_atend_disp_p
		and		nr_atendimento = nr_atendimento_p
		and		nr_sequencia = nr_sequencia_w;

	end if;

	if (coalesce(wheb_usuario_pck.get_ie_commit, 'S') = 'S') then commit; end if;

end if;
end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
