SET client_encoding TO 'UTF8';





CREATE OR REPLACE PROCEDURE inserir_documento_pf ( cd_pessoa_fisica_p text, ie_tipo_doc_p text, ds_arquivo_p text, nm_usuario_p text) AS $body$
DECLARE


nr_sequencia_seq_w	bigint;
nr_sequencia_tipo_doc_w	bigint;
ds_documento_w		varchar(100);

BEGIN

/* ie_tipo_doc_p

CO - Certidão óbito
ES - Escolaridade
PR - Procuração
PC - Cartão convênio
DF - Documento pessoal
PM - Pedido médico

*/
if (ie_tipo_doc_p = 'CO') then

	select	max(nr_sequencia),
		max(ds_documento)
	into STRICT	nr_sequencia_tipo_doc_w,
		ds_documento_w
	from	tipo_documentacao
	where	IE_OBITO = 'S';

elsif (ie_tipo_doc_p = 'ES') then

	select	max(nr_sequencia),
		max(ds_documento)
	into STRICT	nr_sequencia_tipo_doc_w,
		ds_documento_w
	from	tipo_documentacao
	where	IE_ESCOLARIDADE = 'S';

elsif (ie_tipo_doc_p = 'PR') then

	select	max(nr_sequencia),
		max(ds_documento)
	into STRICT	nr_sequencia_tipo_doc_w,
		ds_documento_w
	from	tipo_documentacao
	where	IE_PROCURACAO = 'S';

elsif (ie_tipo_doc_p = 'PC') then

	select	max(nr_sequencia),
		max(ds_documento)
	into STRICT	nr_sequencia_tipo_doc_w,
		ds_documento_w
	from	tipo_documentacao
	where	IE_CART_CONVENIO = 'S';

elsif (ie_tipo_doc_p = 'DF') then

	select	max(nr_sequencia),
		max(ds_documento)
	into STRICT	nr_sequencia_tipo_doc_w,
		ds_documento_w
	from	tipo_documentacao
	where	IE_DOC_PESSOAL = 'S';

elsif (ie_tipo_doc_p = 'PM') then

	select	max(nr_sequencia),
		max(ds_documento)
	into STRICT	nr_sequencia_tipo_doc_w,
		ds_documento_w
	from	tipo_documentacao
	where	IE_PEDIDO_MEDICO = 'S';

end if;

select	nextval('pessoa_documentacao_seq')
into STRICT	nr_sequencia_seq_w
;

insert into PESSOA_DOCUMENTACAO(
	nr_sequencia,
	dt_atualizacao_nrec,
	nm_usuario_nrec,
	dt_atualizacao,
	nm_usuario,
	nr_seq_documento,
	cd_pessoa_fisica,
	ds_arquivo,
	ie_entregue,
	ds_titulo,
	ie_situacao)
values (nr_sequencia_seq_w,
	clock_timestamp(),
	nm_usuario_p,
	clock_timestamp(),
	nm_usuario_p,
	nr_sequencia_tipo_doc_w,
	cd_pessoa_fisica_p,
	ds_arquivo_p,
	'N',
	ds_documento_w,
	'A');

commit;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
-- REVOKE ALL ON PROCEDURE inserir_documento_pf ( cd_pessoa_fisica_p text, ie_tipo_doc_p text, ds_arquivo_p text, nm_usuario_p text) FROM PUBLIC;

