CREATE OR REPLACE PROCEDURE inserir_dt_chegada_paciente ( nr_atendimento_p atendimento_paciente.nr_atendimento%type) AS $body$
DECLARE


dt_entrada_w	atendimento_paciente.dt_entrada%type;


BEGIN

if (nr_atendimento_p IS NOT NULL AND nr_atendimento_p::text <> '')then
	
	select	max(dt_entrada)
	into STRICT 	dt_entrada_w
	from	atendimento_paciente
	where 	nr_atendimento = nr_atendimento_p;
	
	if ((dt_entrada_w IS NOT NULL AND dt_entrada_w::text <> '') and dt_entrada_w < clock_timestamp())then

		update	atendimento_paciente
		set	dt_chegada_paciente = dt_entrada_w
		where	nr_atendimento	= nr_atendimento_p;

		commit;
	end if;

    CALL inserir_dt_validade_case(nr_atendimento_p);
	
end if;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
