CREATE OR REPLACE PROCEDURE inserir_dt_geracao_dmed ( nr_sequencia_p bigint, ie_opcao_p text) AS $body$
DECLARE


/* Opções
I - Inserir data de geração
R - Remover a data de geração
*/
BEGIN

if (ie_opcao_p = 'I') then
	update dmed set dt_geracao = clock_timestamp() where nr_sequencia = nr_sequencia_p;
elsif (ie_opcao_p = 'R') then
	update dmed set dt_geracao  = NULL where nr_sequencia = nr_sequencia_p;
end if;

commit;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
