CREATE OR REPLACE PROCEDURE inserir_etapa_checkup_jv ( nr_seq_checkup_etapa_p bigint, ds_itens_p text, nm_usuario_p text) AS $body$
DECLARE


lista_informacao_w		varchar(32000);
ie_contador_w			bigint	:= 0;
tam_lista_w			bigint;
ie_pos_virgula_w		smallint;
ie_pos_traco_w			bigint;
lista_inf_w			varchar(100);
ie_ultima_virgula_w	smallint;
nr_seq_etapa_w		bigint;


BEGIN

if (ds_itens_p IS NOT NULL AND ds_itens_p::text <> '') then
	tam_lista_w			:= length(ds_itens_p);
	ie_ultima_virgula_w	:= instr(ds_itens_p,',',-1);
	
	if (tam_lista_w = ie_ultima_virgula_w) then
		lista_informacao_w	:=	ds_itens_p;
	else
		lista_informacao_w	:=	ds_itens_p || ',';
	end if;
end if;

while	(lista_informacao_w IS NOT NULL AND lista_informacao_w::text <> '') or
	ie_contador_w > 200 loop
	begin
	tam_lista_w			:= length(lista_informacao_w);
	ie_pos_virgula_w		:= position(',' in lista_informacao_w);	
	nr_seq_etapa_w		:= substr(lista_informacao_w,1,ie_pos_virgula_w - 1);
	
	/* Obter a sequencia lida */

	if (ie_pos_virgula_w <> 0) then
		begin		
		lista_informacao_w	:= substr(lista_informacao_w,(ie_pos_virgula_w + 1),tam_lista_w);
		end;
	end if;

	if (nr_seq_checkup_etapa_p > 0) and (coalesce(nr_seq_etapa_w,0) > 0)then
				
		CALL inserir_etapa_checkup(nr_seq_checkup_etapa_p,nr_seq_etapa_w,nm_usuario_p);
		
	end if;	
	end;
end loop;

commit;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
