CREATE OR REPLACE PROCEDURE inserir_excluir_protoc_canc ( NR_SEQ_AGENDA_CONS_P bigint, NR_SEQ_AGENDA_EXAME_P bigint, IE_INSERIR_P text, NM_USUARIO_P text ) AS $body$
DECLARE


nr_sequencia_w	 bigint;


BEGIN
/*

IE_INSERIR_P
'I'  - Cancelamento ( insere o protocolo )
'E' - Reverter Cancelamento ( exclui o protocolo )
*/
-- Cancelamento de um agendamento:
if ( IE_INSERIR_P = 'I' and ( (NR_SEQ_AGENDA_CONS_P IS NOT NULL AND NR_SEQ_AGENDA_CONS_P::text <> '') or (NR_SEQ_AGENDA_EXAME_P IS NOT NULL AND NR_SEQ_AGENDA_EXAME_P::text <> '')) ) then

	insert into AGENDA_PROTOC_CANC(
		NR_SEQUENCIA,
		DT_ATUALIZACAO_NREC,
		NM_USUARIO_NREC,
		NR_SEQ_AGENDA_CONS,
		NR_SEQ_AGENDA_EXAME,
		NR_ANO,
		NR_PROTOCOLO
	) values (
		nextval('agenda_protoc_canc_seq'),
		clock_timestamp(),
		NM_USUARIO_P,
		NR_SEQ_AGENDA_CONS_P,
		NR_SEQ_AGENDA_EXAME_P,
		PKG_DATE_UTILS.extract_field('YEAR',clock_timestamp()),
		nextval('agenda_protoc_canc_prot_seq')
	);

-- Reverter Cancelamento:
elsif ( IE_INSERIR_P = 'E' ) then

	if (NR_SEQ_AGENDA_CONS_P IS NOT NULL AND NR_SEQ_AGENDA_CONS_P::text <> '') then
		select	max(NR_SEQUENCIA)
		into STRICT	nr_sequencia_w
		from	agenda_protoc_canc
		where	nr_seq_agenda_cons = NR_SEQ_AGENDA_CONS_P;
	elsif (NR_SEQ_AGENDA_EXAME_P IS NOT NULL AND NR_SEQ_AGENDA_EXAME_P::text <> '') then
		select	max(NR_SEQUENCIA)
		into STRICT	nr_sequencia_w
		from	agenda_protoc_canc
		where	nr_seq_agenda_exame = NR_SEQ_AGENDA_EXAME_P;
	end if;

	if (nr_sequencia_w IS NOT NULL AND nr_sequencia_w::text <> '') then
		delete from AGENDA_PROTOC_CANC
		where nr_sequencia = nr_sequencia_w;
	end if;

end if;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
