SET client_encoding TO 'UTF8';





CREATE OR REPLACE PROCEDURE inserir_executivo_checkup ( nm_executivo_p text, cd_empresa_p bigint, nm_usuario_p text, nr_cpf_p text, qt_PERIODICIDADE_p text, ie_sexo_p text, dt_nascimento_p text, ds_executivos_p INOUT text, dt_inicio_vig_p timestamp default null, dt_fim_vig_p timestamp default null) AS $body$
DECLARE


nr_sequencia_w		bigint;
cont_w			bigint;
qt_dt_fim		smallint;
ds_executivos_w		varchar(2000);
cont_cpf_w		bigint;
dt_fim_w		timestamp;
dt_inicio_w		timestamp;
nr_seq_empresa_w	bigint;
dt_nascimento_w		timestamp;
nm_executivo_w		varchar(255);
ie_sexo_w		varchar(1);


BEGIN
select	replace(nm_executivo_p,chr(34),''),
	substr(replace(ie_sexo_p,chr(34),''),1,1)
into STRICT	nm_executivo_w,
	ie_sexo_w
;

if (position(' ' in dt_nascimento_p) = 11) then
	select	to_date(substr(dt_nascimento_p,1,position(' ' in dt_nascimento_p)),'dd/mm/yyyy')
	into STRICT	dt_nascimento_w
	;
elsif (position(' ' in dt_nascimento_p) = 9) then
	select	to_date(substr(dt_nascimento_p,1,position(' ' in dt_nascimento_p)),'dd/mm/yy')
	into STRICT	dt_nascimento_w
	;
end if;

if (nm_executivo_w IS NOT NULL AND nm_executivo_w::text <> '') and (cd_empresa_p IS NOT NULL AND cd_empresa_p::text <> '') then
	begin

	select	nextval('checkup_imp_pessoa_seq')
	into STRICT	nr_sequencia_w
	;

	select	count(*)
	into STRICT	cont_w
	from	checkup_imp_pessoa
	where	upper(trim(both nm_executivo_w)) = nm_pessoa_fisica;

	select 	max(nr_sequencia)
	into STRICT	nr_seq_empresa_w
	from   	empresa_contrato_checkup
	where  	dt_final >= clock_timestamp()
	and    	cd_empresa = cd_empresa_p;

	if (nr_seq_empresa_w > 0) then
		select	dt_final,
			dt_inicial
		into STRICT	dt_fim_w,
			dt_inicio_w
		from	EMPRESA_CONTRATO_CHECKUP
		where	nr_sequencia = nr_seq_empresa_w;
	end if;

	if (cont_w > 0) then
		insert into 	w_checkup_duplica_exec(nr_sequencia,
							nm_usuario,
							nm_pessoa_fisica,
							nr_cpf,
							ie_vinculo,
							cd_empresa_ref,
							nr_seq_checkup_imp,
							dt_atualizacao,
							dt_atualizacao_nrec,
							qt_periodicidade,
							dt_inicio_vigencia,
							dt_fim,
							ie_sexo,
							dt_nascimento
							)
				values (nextval('w_checkup_duplica_exec_seq'),
							nm_usuario_p,
							upper(trim(both nm_executivo_w)),
							nr_cpf_p,
							'P',
							cd_empresa_p,
							nr_sequencia_w,
							clock_timestamp(),
							clock_timestamp(),
							qt_PERIODICIDADE_p,
							coalesce(dt_inicio_vig_p,dt_inicio_w),
							coalesce(dt_fim_vig_p,dt_fim_w),
							ie_sexo_w,
							dt_nascimento_w);
	end if;

	if (cont_w > 0) then
		ds_executivos_w	:= ds_executivos_w || ' - ' || upper(trim(both nm_executivo_w));
	else
		insert 	into  checkup_imp_pessoa(
			nr_sequencia,
			dt_atualizacao,
			nm_usuario,
			dt_atualizacao_nrec,
			nm_usuario_nrec,
			nm_pessoa_fisica,
			cd_empresa_ref,
			dt_inicio,
			dt_inicio_vigencia,
			dt_fim,
			nr_cpf,
			qt_periodicidade,
			ie_sexo,
			dt_nascimento)
		values (
			nr_sequencia_w,
			clock_timestamp(),
			nm_usuario_p,
			clock_timestamp(),
			nm_usuario_p,
			upper(trim(both nm_executivo_w)),
			cd_empresa_p,
			clock_timestamp(),
			coalesce(dt_inicio_vig_p,dt_inicio_w),
			coalesce(dt_fim_vig_p,dt_fim_w),
			nr_cpf_p,
			qt_periodicidade_p,
			ie_sexo_w,
			dt_nascimento_w);
		commit;
	end if;
	end;
end if;
commit;

ds_executivos_p	:= substr(ds_executivos_w,1,255);

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
-- REVOKE ALL ON PROCEDURE inserir_executivo_checkup ( nm_executivo_p text, cd_empresa_p bigint, nm_usuario_p text, nr_cpf_p text, qt_PERIODICIDADE_p text, ie_sexo_p text, dt_nascimento_p text, ds_executivos_p INOUT text, dt_inicio_vig_p timestamp default null, dt_fim_vig_p timestamp default null) FROM PUBLIC;

