SET client_encoding TO 'UTF8';





CREATE OR REPLACE PROCEDURE inserir_exec_duplic_checkup ( nm_executivo_p text, cd_empresa_p bigint, nm_usuario_p text, nr_cpf_p text, qt_PERIODICIDADE_p text, nr_seq_checkup_imp_p bigint, nr_sequencia_p bigint) AS $body$
DECLARE


nr_sequencia_w		bigint;
cont_w			bigint;
qt_dt_fim		smallint;
ds_executivos_w		varchar(2000);
cont_cpf_w		bigint;
dt_fim_w		timestamp;
dt_inicio_w		timestamp;
nr_seq_empresa_w	bigint;
dt_fim_vig_w		timestamp;
dt_inicio_vig_w		timestamp;
dt_nascimento_w		timestamp;
ie_sexo_w		varchar(1);
cd_pessoa_fisica_w	varchar(10);
qt_periodicidade_w	bigint;


BEGIN

select	(trim(both qt_periodicidade_p))::numeric
into STRICT	qt_periodicidade_w
;

if (nm_executivo_p IS NOT NULL AND nm_executivo_p::text <> '') and (cd_empresa_p IS NOT NULL AND cd_empresa_p::text <> '') then
	begin

	select	max(cd_pessoa_fisica)
	into STRICT	cd_pessoa_fisica_w
	from	checkup_imp_pessoa
	where	cd_empresa_ref = cd_empresa_p
	and	nr_cpf = nr_cpf_p;

	update	checkup_imp_pessoa
	set	dt_fim	= clock_timestamp()
	where	cd_empresa_ref = cd_empresa_p
	and	nr_cpf = nr_cpf_p
	and	dt_fim >= clock_timestamp();


	select	nextval('checkup_imp_pessoa_seq')
	into STRICT	nr_sequencia_w
	;

	select	max(dt_inicio_vigencia),
		max(dt_fim),
		max(dt_nascimento),
		max(ie_sexo)
	into STRICT	dt_inicio_vig_w,
		dt_fim_vig_w,
		dt_nascimento_w,
		ie_sexo_w
	from	w_checkup_duplica_exec
	where	nr_sequencia = nr_sequencia_p;

	select 	max(nr_sequencia)
	into STRICT	nr_seq_empresa_w
	from   	empresa_contrato_checkup
	where  	dt_final >= clock_timestamp()
	and    	cd_empresa = cd_empresa_p;

	if (nr_seq_empresa_w > 0) then
		select	dt_final,
			dt_inicial
		into STRICT	dt_fim_w,
			dt_inicio_w
		from	EMPRESA_CONTRATO_CHECKUP
		where	nr_sequencia = nr_seq_empresa_w;
	end if;


		insert 	into  checkup_imp_pessoa(
			nr_sequencia,
			dt_atualizacao,
			nm_usuario,
			dt_atualizacao_nrec,
			nm_usuario_nrec,
			nm_pessoa_fisica,
			cd_empresa_ref,
			dt_inicio,
			dt_fim,
			dt_inicio_vigencia,
			nr_cpf,
			qt_periodicidade,
			dt_nascimento,
			ie_sexo,
			cd_pessoa_fisica)
		values (
			nr_sequencia_w,
			clock_timestamp(),
			nm_usuario_p,
			clock_timestamp(),
			nm_usuario_p,
			upper(trim(both nm_executivo_p)),
			cd_empresa_p,
			clock_timestamp(),
			coalesce(dt_fim_vig_w,dt_fim_w),
			coalesce(dt_inicio_vig_w,dt_inicio_w),
			nr_cpf_p,
			qt_periodicidade_w,
			dt_nascimento_w,
			ie_sexo_w,
			cd_pessoa_fisica_w);
		commit;

	end;
end if;
commit;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
