CREATE OR REPLACE PROCEDURE inserir_funcao_lote_param (nr_seq_lote_p bigint, cd_funcao_p bigint, nm_usuario_p text) AS $body$
DECLARE

 
 
c01 CURSOR FOR 
SELECT	distinct 
		a.nr_seq_parametro 
from	html_param_to_rule a 
where	a.cd_funcao = cd_funcao_p 
and		(a.dt_liberacao IS NOT NULL AND a.dt_liberacao::text <> '');		
 
nr_sequencia_w		bigint;							
									 
BEGIN 
 
select	nextval('lote_par_regra_html_funcao_seq') 
into STRICT	nr_sequencia_w
;
 
insert into lote_par_regra_html_funcao(nr_sequencia, 
	nm_usuario, 
	dt_atualizacao, 
	nm_usuario_nrec, 
	dt_atualizacao_nrec, 
	nr_seq_lote, 
	cd_funcao) 
values (nr_sequencia_w, 
	nm_usuario_p, 
	clock_timestamp(), 
	nm_usuario_p, 
	clock_timestamp(), 
	nr_seq_lote_p, 
	cd_funcao_p);
	 
for r_c01 in c01 loop 
	insert into lote_par_regra_html_item(nr_sequencia, 
		nm_usuario, 
		dt_atualizacao, 
		nm_usuario_nrec, 
		dt_atualizacao_nrec, 
		nr_seq_funcao_lote, 
		nr_seq_parametro, 
		cd_funcao) 
	values (nextval('lote_par_regra_html_item_seq'), 
		nm_usuario_p, 
		clock_timestamp(), 
		nm_usuario_p, 
		clock_timestamp(), 
		nr_sequencia_w, 
		r_c01.nr_seq_parametro, 
		cd_funcao_p);
end loop;
 
commit;
 
end;
