CREATE OR REPLACE PROCEDURE inserir_ganho_perda_grafico ( nr_atendimento_p bigint, nr_cirurgia_p bigint, nr_seq_tipo_p bigint, cd_setor_atendimento_p bigint, dt_ocorrencia_p timestamp, qt_volume_p bigint, ie_liberado_p text, nr_hora_p bigint, ds_observacao_p text, nm_usuario_p text, qt_ocorrencia_p bigint, nr_seq_pepo_p bigint) AS $body$
DECLARE


nr_sequencia_w		bigint;
cd_pessoa_fisica_w	varchar(10);


BEGIN

select	max(cd_pessoa_fisica)
into STRICT	cd_pessoa_fisica_w
from	usuario
where	nm_usuario	= nm_usuario_p;

select	nextval('atendimento_perda_ganho_seq')
into STRICT	nr_sequencia_w
;

insert into atendimento_perda_ganho(
	nr_sequencia,
	nr_atendimento,
	dt_atualizacao,
	nm_usuario,
	nr_seq_tipo,
	qt_volume,
	ds_observacao,
	dt_medida,
	cd_turno,
	cd_setor_atendimento,
	cd_profissional,
	nr_cirurgia,
	ie_situacao,
	dt_liberacao,
	nr_hora,
	qt_ocorrencia,
	nr_seq_pepo)
values (	nr_sequencia_w,
	nr_atendimento_p,
	clock_timestamp(),
	nm_usuario_p,
	nr_seq_tipo_p,
	coalesce(qt_volume_p,0),
	ds_observacao_p,
	dt_ocorrencia_p,
	null,
	cd_setor_atendimento_p,
	cd_pessoa_fisica_w,
	nr_cirurgia_p,
	'A',
	CASE WHEN ie_liberado_p='S' THEN clock_timestamp()  ELSE null END ,
	nr_hora_p,
	coalesce(qt_ocorrencia_p,0),
	nr_seq_pepo_p);
	
if (coalesce(qt_volume_p,0)	> 0) then
	CALL gerar_lancamento_automatico(nr_atendimento_p,null,381,nm_usuario_p,null,nr_seq_tipo_p,null,null,null,null);
end if;

commit;
	
end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
