CREATE OR REPLACE PROCEDURE inserir_hemocomponente_inutil (nr_seq_doacao_p bigint, nr_seq_motivo_p bigint, ds_observacao_p text, cd_estabelecimento_p bigint, nm_pasta_p text, nm_usuario_p text, nr_seq_insp_bolsa_p bigint) AS $body$
DECLARE


ie_inutilizar_bolsa_w san_insp_integridade.ie_inutilizar_bolsa%type;
cd_pessoa_fisica_w	varchar(10);
nr_seq_inutil_w		bigint;
nm_pasta_w		varchar(3);


BEGIN

select 	cd_pessoa_fisica
into STRICT	cd_pessoa_fisica_w
from	usuario
where	nm_usuario = nm_usuario_p;

select	nextval('san_inutilizacao_seq')
into STRICT	nr_seq_inutil_w
;

begin
  select ie_inutilizar_bolsa
  into STRICT   ie_inutilizar_bolsa_w
  from   san_insp_integridade
  where nr_sequencia = nr_seq_insp_bolsa_p;
exception
  when no_data_found then ie_inutilizar_bolsa_w := 'N';
  when too_many_rows then raise;
end;

if ((nr_seq_doacao_p IS NOT NULL AND nr_seq_doacao_p::text <> '') and ie_inutilizar_bolsa_w = 'S') then

	if (upper(nm_pasta_p) = 'EXAMES_TS') then				nm_pasta_w := 'E';
	elsif (upper(nm_pasta_p) = 'PRODUCAO_ITENS_EMPRESTADOS_TS') then	nm_pasta_w := 'IE';
	elsif (upper(nm_pasta_p) = 'PRODUCAO_TS') then			nm_pasta_w := 'P';	
	elsif (upper(nm_pasta_p) = 'ESTOQUE_TS') then				nm_pasta_w := 'Q';
	elsif (upper(nm_pasta_p) = 'RESERVA_TS') then				nm_pasta_w := 'R';
	elsif (upper(nm_pasta_p) = 'EMPREST_SAIDA_TS') then			nm_pasta_w := 'S';
	elsif (upper(nm_pasta_p) = 'TRANSFUSAO_TS') then			nm_pasta_w := 'T';
	end if;
	
	insert into san_inutilizacao(
		nr_sequencia,
		dt_inutilizacao,
		nr_seq_motivo_inutil,
		dt_atualizacao,
		nm_usuario,
		cd_pf_realizou,
		ds_observacao,
		dt_atualizacao_nrec,
		nm_usuario_nrec,
		cd_estabelecimento)
	values ( nr_seq_inutil_w,
		clock_timestamp(),
		nr_seq_motivo_p,
		clock_timestamp(),
		nm_usuario_p,
		cd_pessoa_fisica_w,
		ds_observacao_p,
		clock_timestamp(),
		nm_usuario_p, 
		cd_estabelecimento_p);

	update	san_producao
	set	nr_seq_inutil 		= nr_seq_inutil_w,
		ie_local_inutilizacao	= nm_pasta_w,
		nm_usuario		= nm_usuario_p,
		dt_atualizacao		= clock_timestamp()
	where	nr_seq_doacao		= nr_seq_doacao_p;
end if;

commit;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
-- REVOKE ALL ON PROCEDURE inserir_hemocomponente_inutil (nr_seq_doacao_p bigint, nr_seq_motivo_p bigint, ds_observacao_p text, cd_estabelecimento_p bigint, nm_pasta_p text, nm_usuario_p text, nr_seq_insp_bolsa_p bigint) FROM PUBLIC;

