CREATE OR REPLACE PROCEDURE inserir_historico_ordem_pic ( nr_seq_requisito_p LATAM_REQUISITO_OS.NR_SEQUENCIA%TYPE, ds_relat_tecnico_p MAN_ORDEM_SERV_TECNICO.DS_RELAT_TECNICO%TYPE ) AS $body$
DECLARE


nr_seq_hist_w       MAN_ORDEM_SERV_TECNICO.NR_SEQUENCIA%TYPE;
nm_usuario_w        MAN_ORDEM_SERV_TECNICO.NM_USUARIO%TYPE := WHEB_USUARIO_PCK.GET_NM_USUARIO;
nr_seq_ordem_serv_w MAN_ORDEM_SERV_TECNICO.NR_SEQ_ORDEM_SERV%TYPE;


BEGIN
if (nr_seq_requisito_p IS NOT NULL AND nr_seq_requisito_p::text <> '') then
	SELECT coalesce(MAX(nr_seq_ordem_serv), 0)
	INTO STRICT   nr_seq_ordem_serv_w
	FROM   latam_requisito_os
	WHERE  nr_seq_requisito = nr_seq_requisito_p
	AND    ie_tipo_os = '1';

	SELECT 	nextval('man_ordem_serv_tecnico_seq')
	INTO STRICT	nr_seq_hist_w
	;

	if (nr_seq_hist_w IS NOT NULL AND nr_seq_hist_w::text <> '' AND nr_seq_ordem_serv_w > 0) then
		INSERT into MAN_ORDEM_SERV_TECNICO(
			nr_sequencia,
			nr_seq_ordem_serv,
			dt_atualizacao,
			dt_historico,
			dt_liberacao,
			nm_usuario,
			nr_seq_tipo,
			ie_origem,
			ds_relat_tecnico
		) VALUES (
			nr_seq_hist_w,
			nr_seq_ordem_serv_w,
			clock_timestamp(),
			clock_timestamp(),
			clock_timestamp(),
			nm_usuario_w,
			1,
			'I',
			ds_relat_tecnico_p
		);

		COMMIT;
	end if;
end if;
end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
