CREATE OR REPLACE PROCEDURE inserir_hist_envio_autorizacao (nr_seq_autorizacao_p bigint, nm_usuario_p text, ds_destino_p text, ds_observacao_p text) AS $body$
DECLARE


qt_registro_envio_w		bigint := 0;
nr_seq_autor_envio_w		bigint;
nr_seq_estagio_encaminhado_w	bigint;
ie_atualiza_estagio_autor_w	varchar(15) := 'N';

BEGIN
ie_atualiza_estagio_autor_w := obter_param_usuario(3004, 145, Obter_Perfil_ativo, nm_usuario_p, wheb_usuario_pck.get_cd_estabelecimento, ie_atualiza_estagio_autor_w);

if (coalesce(ie_atualiza_estagio_autor_w,'N') = 'S') then
	begin
	select	count(*)
	into STRICT	qt_registro_envio_w
	from	autorizacao_convenio_envio b
	where	b.nr_seq_autorizacao = nr_seq_autorizacao_p;

	begin
	select	max(nr_sequencia)
	into STRICT	nr_seq_estagio_encaminhado_w
	from	estagio_autorizacao
	where	ie_interno = '5'
	and	coalesce(ie_situacao,'A') = 'A'
	and	OBTER_EMPRESA_ESTAB(wheb_usuario_pck.get_cd_estabelecimento) = cd_empresa;
	exception
	when others then
		nr_seq_estagio_encaminhado_w := 0;
	end;

	if (coalesce(qt_registro_envio_w,0) = 0)and (nr_seq_estagio_encaminhado_w > 0) then

		update	autorizacao_convenio
		set	nr_seq_estagio 	= nr_seq_estagio_encaminhado_w,
			nm_usuario	= nm_usuario_p,
			dt_atualizacao	= clock_timestamp()
		where	nr_sequencia	= nr_seq_autorizacao_p
		and	nr_seq_estagio	<> nr_seq_estagio_encaminhado_w;

	end if;
	end;
end if;

insert into autorizacao_convenio_envio(
		nr_sequencia,
		dt_atualizacao,
		nm_usuario,
		dt_atualizacao_nrec,
		nm_usuario_nrec,
		nr_seq_autorizacao,
		dt_envio,
		ds_destino,
		ds_observacao)
values (nextval('autorizacao_convenio_envio_seq'),
		clock_timestamp(),
		nm_usuario_p,
		clock_timestamp(),
		nm_usuario_p,
		nr_seq_autorizacao_p,
		clock_timestamp(),
		ds_destino_p,
		ds_observacao_p);


commit;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
