CREATE OR REPLACE PROCEDURE inserir_imagem_laudo_paciente ( nr_sequencia_p bigint, ds_arquivo_p text, nm_usuario_p text, ds_imagem_p text DEFAULT '') AS $body$
DECLARE


nr_seq_imagem_w	bigint;


BEGIN
if (nr_sequencia_p IS NOT NULL AND nr_sequencia_p::text <> '') and (ds_arquivo_p IS NOT NULL AND ds_arquivo_p::text <> '') and (nm_usuario_p IS NOT NULL AND nm_usuario_p::text <> '') then
	begin

	select (coalesce(max(nr_seq_imagem),0) + 1)
	into STRICT	nr_seq_imagem_w
	from	laudo_paciente_imagem
	where	nr_sequencia = nr_sequencia_p;

	insert	into laudo_paciente_imagem(
		nr_sequencia,
		nr_seq_imagem,
		dt_atualizacao,
		nm_usuario,
		ds_imagem,
		ds_arquivo_imagem)
	values (
		nr_sequencia_p,
		nr_seq_imagem_w,
		clock_timestamp(),
		nm_usuario_p,
		ds_imagem_p,
		ds_arquivo_p);
	end;
end if;
commit;
end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
