CREATE OR REPLACE PROCEDURE inserir_indicacoes_clinicas ( nr_seq_solic_p bigint, ds_lista_indicacoes_p text, nm_usuario_p text) AS $body$
DECLARE


nr_pos_virgula_w	bigint;
nr_seq_indicacao_w	bigint;
ds_lista_indicacoes_w	varchar(2000);
qt_increm_w		integer := 0;


BEGIN
if (nr_seq_solic_p IS NOT NULL AND nr_seq_solic_p::text <> '') and (ds_lista_indicacoes_p IS NOT NULL AND ds_lista_indicacoes_p::text <> '') and (nm_usuario_p IS NOT NULL AND nm_usuario_p::text <> '') then
	begin

	ds_lista_indicacoes_w := ds_lista_indicacoes_p;

	while(ds_lista_indicacoes_w IS NOT NULL AND ds_lista_indicacoes_w::text <> '') and (qt_increm_w <= 1000) loop
		begin

		qt_increm_w := (qt_increm_w + 1);

		nr_pos_virgula_w	:= position(',' in ds_lista_indicacoes_w);

		if (nr_pos_virgula_w > 0) then
			begin
			nr_seq_indicacao_w	:= (substr(ds_lista_indicacoes_w,0,nr_pos_virgula_w-1))::numeric;
			ds_lista_indicacoes_w	:= substr(ds_lista_indicacoes_w,nr_pos_virgula_w+1,length(ds_lista_indicacoes_w));
			end;
		else
			begin
			nr_seq_indicacao_w	:= (ds_lista_indicacoes_w)::numeric;
			ds_lista_indicacoes_w	:= null;
			end;
		end if;

		if (coalesce(nr_seq_indicacao_w,0) > 0) then
			begin
			CALL inserir_indicacao_clinica(
			nr_seq_solic_p,
			nr_seq_indicacao_w,
			nm_usuario_p);
			end;
		end if;
		end;
	end loop;
	end;
end if;
commit;
end;
$body$
