SET client_encoding TO 'UTF8';





CREATE OR REPLACE PROCEDURE inserir_informacao_apap ( nr_seq_apap_p bigint, nr_seq_grupo_apap_p bigint, ds_lista_inf_p text, nm_usuario_p text) AS $body$
DECLARE


lista_informacao_w		varchar(1000);
ie_contador_w			bigint	:= 0;
tam_lista_w			bigint;
ie_pos_virgula_w		smallint;
nr_seq_inf_w			bigint;
nr_sequencia_w			bigint;
cd_intervalo_w			varchar(7);


BEGIN

lista_informacao_w	:= ds_lista_inf_p;

while	(lista_informacao_w IS NOT NULL AND lista_informacao_w::text <> '') or
	ie_contador_w > 200 loop
	begin

	tam_lista_w			:= length(lista_informacao_w);
	ie_pos_virgula_w		:= position(',' in lista_informacao_w);

	/* Obter a sequência lida */

	if (ie_pos_virgula_w <> 0) then
		nr_seq_inf_w		:= substr(lista_informacao_w,1,(ie_pos_virgula_w - 1));
		lista_informacao_w	:= substr(lista_informacao_w,(ie_pos_virgula_w + 1),tam_lista_w);
	end if;

	/* Obtem a nova sequência */

	select	nextval('pep_apap_inf_seq')
	into STRICT	nr_sequencia_w
	;

	insert into pep_apap_inf(
		nr_sequencia,
		dt_atualizacao,
		nm_usuario,
		dt_atualizacao_nrec,
		nm_usuario_nrec,
		nr_seq_grupo_apap,
		nr_seq_apres,
		nr_seq_inf,
		ie_manter_nulo,
		ie_grafico)
	values (nr_sequencia_w,
		clock_timestamp(),
		nm_usuario_p,
		clock_timestamp(),
		nm_usuario_p,
		nr_seq_grupo_apap_p,
		999,
		nr_seq_inf_w,
		'S',
		'N');

	ie_contador_w	:= ie_contador_w + 1;

	end;
end loop;

commit;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
