CREATE OR REPLACE PROCEDURE inserir_inf_adic_hs ( cd_pessoa_fisica_p text, nr_seq_item_inf_p bigint, ds_valor_p text, nm_usuario_p text, dt_resultado_p timestamp default null) AS $body$
DECLARE


nr_seq_pp_w	    bigint;
nr_seq_ppi_w	bigint;
ie_tipo_campo_w	varchar(3);
nr_sequencia_w	bigint;


BEGIN

select	coalesce(max(nr_sequencia),0)
into STRICT	nr_sequencia_w
from	HIST_SAUDE_INF_ADIC_ITEM
where	NR_SEQ_INF_ADIC	= nr_seq_item_inf_p
and	    cd_pessoa_fisica = cd_pessoa_fisica_p
and	    nm_usuario_nrec	= nm_usuario_p;

select	ie_tipo_campo
into STRICT	ie_tipo_campo_w
from	historico_saude_inf_adic
where	nr_sequencia = nr_seq_item_inf_p;

if (nr_sequencia_w = 0) then
	/* inserir o item (insert) */

	insert	into HIST_SAUDE_INF_ADIC_ITEM(
		nr_sequencia,
		dt_atualizacao,
		nm_usuario,
		dt_atualizacao_nrec,
		nm_usuario_nrec,
		NR_SEQ_INF_ADIC,
		nr_inf_adic,
		ds_inf_adic,
		dt_inf_adic,
		cd_pessoa_fisica)
	values (
		nextval('hist_saude_inf_adic_item_seq'),
		clock_timestamp(),
		nm_usuario_p,
		clock_timestamp(),
		nm_usuario_p,
		nr_seq_item_inf_p,
		CASE WHEN ie_tipo_campo_w='N' THEN (ds_valor_p)::numeric   ELSE null END ,
		CASE WHEN ie_tipo_campo_w='A' THEN ds_valor_p  ELSE null END ,
		CASE WHEN ie_tipo_campo_w='D' THEN coalesce(to_date(ds_valor_p,'dd/mm/yyyy hh24:mi:ss'),dt_resultado_p)  ELSE null END ,
		cd_pessoa_fisica_p);
	commit;
else
	/* atualizar o item (update) */

	update	HIST_SAUDE_INF_ADIC_ITEM
	set	nr_inf_adic	=	CASE WHEN ie_tipo_campo_w='N' THEN (ds_valor_p)::numeric   ELSE null END ,
		ds_inf_adic	=	CASE WHEN ie_tipo_campo_w='A' THEN ds_valor_p  ELSE null END ,
		dt_inf_adic	=	CASE WHEN ie_tipo_campo_w='D' THEN coalesce(to_date(ds_valor_p,'dd/mm/yyyy hh24:mi:ss'),dt_resultado_p)  ELSE null END ,
		nm_usuario	=	nm_usuario_p,
		dt_atualizacao	=	clock_timestamp()
	where	nr_sequencia	=	nr_sequencia_w;
	commit;
end if;


commit;

end;
$body$
